import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import "../../styles/SpecialOPS.css";

import s1 from "../../assets/gallery/images/gallery30.jpg";
import s2 from "../../assets/gallery/images/gallery31.jpg";
import s3 from "../../assets/gallery/images/gallery32.jpg";
import s4 from "../../assets/gallery/images/gallery33.jpg";
import s5 from "../../assets/gallery/images/gallery34.jpg";
import s6 from "../../assets/gallery/images/gallery35.jpg";
import s7 from "../../assets/gallery/images/gallery36.jpg";
import s8 from "../../assets/gallery/images/gallery37.jpg";
import s9 from "../../assets/gallery/images/gallery38.jpg";
import s10 from "../../assets/gallery/images/gallery39.jpg";
import s11 from "../../assets/gallery/images/gallery40.jpg";
import s12 from "../../assets/gallery/images/gallery41.jpg";

const heroImages = [s1, s2, s3, s4, s5];
const mainImages = [s6, s7, s8, s9, s10, s11, s12];

function SpecialOPS() {
  const [heroIndex, setHeroIndex] = useState(0);
  const [mainIndex, setMainIndex] = useState(0);
  const sectionsRef = useRef([]);

  // Hero & Main slideshows
  useEffect(() => {
    const heroTimer = setInterval(() => setHeroIndex(i => (i + 1) % heroImages.length), 4500);
    const mainTimer = setInterval(() => setMainIndex(i => (i + 1) % mainImages.length), 4000);
    return () => { clearInterval(heroTimer); clearInterval(mainTimer); };
  }, []);

  // Scroll reveal for sections
  useEffect(() => {
    const observer = new IntersectionObserver(entries => {
      entries.forEach(entry => {
        if (entry.isIntersecting) entry.target.classList.add("visible");
      });
    }, { threshold: 0.15 });

    sectionsRef.current.forEach(sec => observer.observe(sec));
  }, []);

  return (
    <div className="rgm-special-page">

      {/* HERO */}
      <section className="rgm-special-hero">
        <div className="hero-image-box">
          <img src={heroImages[heroIndex]} alt="Special Ops Hero" />
        </div>
        <div className="hero-content">
          <h1>Special Operations</h1>
          <p>Oversized â€¢ Specialized â€¢ Mission-Critical Freight by RGM Logistics</p>
        </div>
      </section>

      {/* INTRO */}
      <section className="rgm-special-intro reveal" ref={el => sectionsRef.current[0] = el}>
        <p>
          Some freight requires more than standard planning.
          <strong> RGM Special Operations</strong> is built for complex,
          oversized, and high-value shipments where precision,
          experience, and accountability matter the most.
        </p>
      </section>

      {/* MAIN SLIDESHOW & CREATIVE CARDS */}
      <section className="rgm-special-main reveal" ref={el => sectionsRef.current[1] = el}>
        <h2 className="main-headline">Our Special Operations in Action</h2>
        <p className="main-subtext">
          Precision, technology, and expertise come together to handle every unique freight requirement efficiently.
        </p>

        <div className="main-slideshow-frame">
          <img src={mainImages[mainIndex]} alt="Special Ops Main" />
          <div className="floating-cards">
            <div className="floating-card">âœ… Precision Planning</div>
            <div className="floating-card">ðŸš› Specialized Equipment</div>
            <div className="floating-card">ðŸ“Š Real-time Tracking</div>
          </div>
        </div>
      </section>

      {/* FEATURES */}
      <section className="rgm-special-features reveal" ref={el => sectionsRef.current[2] = el}>
        <h2>Special Ops Key Features</h2>
        <div className="features-grid">
          <div className="feature-card">âœ… Zero-Claim Mindset</div>
          <div className="feature-card">ðŸ“ˆ High Customer Retention</div>
          <div className="feature-card">ðŸš› Access to Newer Specialized Equipment</div>
          <div className="feature-card">ðŸ‘· Qualified, Experienced Drivers Only</div>
          <div className="feature-card">âš“ Port & Industrial Logistics Experience</div>
          <div className="feature-card">ðŸ“¦ Oversized & Project Freight Expertise</div>
        </div>
      </section>

      {/* EQUIPMENT */}
      <section className="rgm-special-equipment reveal" ref={el => sectionsRef.current[3] = el}>
        <h2>Specialized Equipment We Work With</h2>
        <ul>
          <li>RGN (Removable Gooseneck Trailers)</li>
          <li>Stretch Multi-Axle Trailers</li>
          <li>Step Deck Trailers</li>
          <li>Flatbeds for Heavy & Wide Loads</li>
        </ul>
      </section>

      {/* WHY RGM */}
      <section className="rgm-special-why reveal" ref={el => sectionsRef.current[4] = el}>
        <h2>Why RGM Special Ops?</h2>
        <p>
          While RGM is a growing company, our Special Operations division
          focuses on quality over volume. We carefully match freight with
          the right equipment, the right driver, and the right plan â€”
          ensuring safe delivery without shortcuts.
        </p>
      </section>

      {/* CTA */}
      <section className="rgm-special-cta reveal" ref={el => sectionsRef.current[5] = el}>
        <h2 className="cta-headline">Your Challenging Freight â€” Handled With Confidence</h2>
        <p>
          Contact RGM today to discuss your specialized transportation needs.
        </p>
        <div className="cta-buttons">
          <Link to="/contact" className="cta-primary">Get A Quote</Link>
          <Link to="/contact" className="cta-secondary">Click to Call</Link>
        </div>
      </section>

    </div>
  );
}

export default SpecialOPS;
