import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom"; 
import "../../styles/WhyRGM.css";

import g10 from "../../assets/gallery/images/gallery10.jpg";
import g11 from "../../assets/gallery/images/gallery11.jpg";
import g12 from "../../assets/gallery/images/gallery12.jpg";
import g13 from "../../assets/gallery/images/gallery13.jpg";
import g14 from "../../assets/gallery/images/gallery14.jpg";

import g15 from "../../assets/gallery/images/gallery15.jpg";
import g16 from "../../assets/gallery/images/gallery16.jpg";
import g17 from "../../assets/gallery/images/gallery17.jpg";
import g18 from "../../assets/gallery/images/gallery18.jpg";
import g19 from "../../assets/gallery/images/gallery19.jpg";

const heroImages = [g10, g11, g12, g13, g14];
const mainImages = [g15, g16, g17, g18, g19];

function WhyRGM() {
  const [heroIndex, setHeroIndex] = useState(0);
  const [mainIndex, setMainIndex] = useState(0);
  const sectionsRef = useRef([]);

  useEffect(() => {
    const heroTimer = setInterval(() => setHeroIndex(i => (i + 1) % heroImages.length), 5000);
    const mainTimer = setInterval(() => setMainIndex(i => (i + 1) % mainImages.length), 3500);
    return () => { clearInterval(heroTimer); clearInterval(mainTimer); };
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      entries => entries.forEach(entry => entry.isIntersecting && entry.target.classList.add("visible")),
      { threshold: 0.15 }
    );
    sectionsRef.current.forEach(sec => observer.observe(sec));
  }, []);

  return (
    <div className="why-page">

      {/* HERO */}
      <section className="hero">
        <div className="hero-image-box">
          <img src={heroImages[heroIndex]} alt="RGM Hero" />
        </div>
        <div className="hero-content">
          <h1>Why RGM Family</h1>
          <p>At RGM, youâ€™re not just joining a company â€” youâ€™re becoming part of a family that grows together.</p>
          {/* HERO APPLY NOW */}
          <Link to="/joinRGM/ApplyToday" className="apply-now-link">Apply Now</Link>
        </div>
      </section>

      {/* INTRO */}
      <section className="section section-light reveal" ref={el => (sectionsRef.current[0] = el)}>
        <div className="section-inner">
          <p className="lead-text">
            RGM may be a young company, but our foundation is built on <strong>trust, respect, and people-first values</strong>.
          </p>
        </div>
      </section>

      {/* IMAGE FEATURE */}
      <section className="section section-dark reveal" ref={el => (sectionsRef.current[1] = el)}>
        <div className="section-inner">
          <h2 className="section-title">Life at RGM</h2>
          <div className="slideshow-frame floating">
            <img src={mainImages[mainIndex]} alt="RGM Culture" />
          </div>
        </div>
      </section>

      {/* VALUES */}
      <section className="section section-light reveal" ref={el => (sectionsRef.current[2] = el)}>
        <div className="section-inner">
          <h2 className="section-title">Our Values</h2>
          <div className="values-grid">
            {[
              ["ðŸ¤ Family First Culture", "Respect, fairness, and growth matter here."],
              ["ðŸ›¡ Integrity & Trust", "Honesty guides every decision."],
              ["ðŸš› Driver-Focused", "Drivers are the backbone."],
              ["ðŸ“ˆ Growth Together", "Long-term success for everyone."],
              ["ðŸ§  Smart Technology", "Modern tools with human touch."],
              ["ðŸ’™ Support Beyond Work", "Care inside & outside work."]
            ].map(([title, text], i) => (
              <div className="value-card" key={i}>
                <h3>{title}</h3>
                <p>{text}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA */}
      <section className="section section-cta reveal" ref={el => (sectionsRef.current[3] = el)}>
        <div className="cta-box">
          <h2>Letâ€™s Talk About Your Future With RGM</h2>
          <p>Join a company that puts people first.</p>
          {/* CTA APPLY NOW */}
          <Link to="/joinRGM/ApplyToday" className="apply-now-link">Apply Now</Link>
        </div>
      </section>

    </div>
  );
}

export default WhyRGM;
