import React, { useEffect, useState } from "react";
import "../../styles/WhyRGM.css";

/* HERO SLIDESHOW */
import g10 from "../../assets/gallery/images/gallery10.jpg";
import g11 from "../../assets/gallery/images/gallery11.jpg";
import g12 from "../../assets/gallery/images/gallery12.jpg";
import g13 from "../../assets/gallery/images/gallery13.jpg";
import g14 from "../../assets/gallery/images/gallery14.jpg";

/* MAIN SLIDESHOW */
import g15 from "../../assets/gallery/images/gallery15.jpg";
import g16 from "../../assets/gallery/images/gallery16.jpg";
import g17 from "../../assets/gallery/images/gallery17.jpg";
import g18 from "../../assets/gallery/images/gallery18.jpg";
import g19 from "../../assets/gallery/images/gallery19.jpg";

const heroImages = [g10, g11, g12, g13, g14];
const mainImages = [g15, g16, g17, g18, g19];

function WhyRGM() {
  const [heroIndex, setHeroIndex] = useState(0);
  const [mainIndex, setMainIndex] = useState(0);

  useEffect(() => {
    const heroTimer = setInterval(() => {
      setHeroIndex(i => (i + 1) % heroImages.length);
    }, 5000);

    const mainTimer = setInterval(() => {
      setMainIndex(i => (i + 1) % mainImages.length);
    }, 3500);

    return () => {
      clearInterval(heroTimer);
      clearInterval(mainTimer);
    };
  }, []);

  return (
    <div className="why-page">

      {/* HERO */}
      <section className="hero">
        <div className="hero-image-box">
          <img
            src={heroImages[heroIndex]}
            alt="RGM Hero"
            key={heroIndex}
          />
        </div>

        <div className="hero-content">
          <h1>Why RGM Family</h1>
          <p>
            At RGM, youâ€™re not just joining a company â€” youâ€™re becoming part of a
            family that grows together.
          </p>
        </div>
      </section>

      {/* INTRO */}
      <section className="intro">
        <p>
          RGM may be a young company, but our foundation is built on something
          timeless: <strong>trust, respect, and people-first values</strong>.
          We believe success comes from taking care of our drivers, employees,
          and partners like family â€” because when they succeed, we succeed.
        </p>
      </section>

      {/* MAIN SLIDESHOW */}
      <section className="main-slideshow">
        <div className="slideshow-frame">
          <img
            src={mainImages[mainIndex]}
            alt="RGM Culture"
            key={mainIndex}
          />
        </div>
      </section>

      {/* VALUES */}
      <section className="values">

        {[
          ["ðŸ¤ Family First Culture", "Respect, fairness, and growth matter here."],
          ["ðŸ›¡ Integrity & Trust", "Honesty guides every decision we make."],
          ["ðŸš› Driver-Focused", "Drivers are the backbone of RGM."],
          ["ðŸ“ˆ Growth Together", "Long-term success for everyone."],
          ["ðŸ§  Smart Technology", "Modern tools with a human touch."],
          ["ðŸ’™ Support Beyond Work", "We care inside and outside the job."]
        ].map(([title, text], i) => (
          <div className="value-card" key={i}>
            <h3>{title}</h3>
            <p>{text}</p>
          </div>
        ))}

      </section>

      {/* CTA */}
      <section className="cta">
        <h2>Letâ€™s Talk About Your Future With RGM</h2>
        <button>Contact RGM Family</button>
      </section>

    </div>
  );
}

export default WhyRGM;
