import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../../styles/Equipment.css";

// Hero images (Flatbed trucks)
import hero1 from "../../assets/images/hero/flatbed1.jpg";
import hero2 from "../../assets/images/hero/flatbed2.jpg";
import hero3 from "../../assets/images/hero/flatbed3.jpg";
import hero4 from "../../assets/images/hero/flatbed4.jpg";

function Equipment() {
  const heroImages = [hero1, hero2, hero3, hero4];
  const [currentHero, setCurrentHero] = useState(0);

  // Hero slideshow
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentHero((prev) => (prev + 1) % heroImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // Scroll animation for content
  useEffect(() => {
    const animateElements = document.querySelectorAll(
      ".equipment-content p, .equipment-content h2, .equipment-list li"
    );
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("active");
          }
        });
      },
      { threshold: 0.2 }
    );
    animateElements.forEach((el) => observer.observe(el));
    return () => animateElements.forEach((el) => observer.unobserve(el));
  }, []);

  return (
    <div className="equipment-container">

      {/* HERO SECTION */}
      <div
        className="equipment-hero"
        style={{ backgroundImage: `url(${heroImages[currentHero]})` }}
      >
        <div className="hero-overlay">
          <h1>Flatbeds Built for Excellence</h1>
          <p>Drive with Pride – Safe, Efficient, and Comfortable</p>
          <Link to="/join/apply" className="hero-apply-btn">
            Apply Now
          </Link>
        </div>
      </div>

      {/* MAIN CONTENT */}
      <div className="equipment-content">
        <p className="intro-text">
          At our company, flatbeds aren’t just trucks — they’re a commitment to
          excellence. From driver comfort to state-of-the-art equipment, our fleet
          ensures every mile is efficient, safe, and rewarding.
        </p>

        <h2>Why Our Flatbeds Stand Out</h2>
        <ul className="equipment-list">
          <li>Leather air-ride seats with lumbar support for fatigue-free driving</li>
          <li>CB radios & Trimble Communication System with electronic logs</li>
          <li>Power mirrors & collision-avoidance safety systems</li>
          <li>Premium audio system for comfort on long hauls</li>
          <li>Full maintenance program: trucks washed weekly, regularly inspected</li>
          <li>Manual & Automatic transmission options</li>
          <li>Flatbed-specific tie-down equipment for maximum load safety</li>
          <li className="highlight">Employee-first approach: comfort, safety, and support</li>
        </ul>

        <h2>Attention to Driver Comfort</h2>
        <p>
          Every truck is designed with the driver in mind. Anti-fatigue seats,
          climate control, LED lighting, and advanced safety features ensure that
          you can focus on the road, knowing your comfort and safety are our priority.
        </p>

        <h2>Fleet Maintenance Excellence</h2>
        <p>
          Our flatbeds are meticulously maintained, ensuring reliability and
          professionalism on every route. Clean, efficient, and fully equipped —
          our trucks represent the gold standard in flatbed trucking.
        </p>

        {/* CTA BOX */}
        <div className="bottom-apply">
          <h3>Join the Flatbed Leaders</h3>
          <p>Apply today and drive the fleet that prioritizes you!</p>
          <Link to="/join/apply" className="bottom-apply-btn">
            Apply Now
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Equipment;
