import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../../styles/Equipment.css";

// Hero images
import hero1 from "../../assets/images/equipment/hero1.jpg";
import hero2 from "../../assets/images/equipment/hero2.jpg";
import hero3 from "../../assets/images/equipment/hero3.jpg";
import hero4 from "../../assets/images/equipment/hero4.jpg";

// Optional gallery images
// import truck1 from "../../assets/images/equipment/truck1.jpg";
// import truck2 from "../../assets/images/equipment/truck2.jpg";

function Equipment() {
  const heroImages = [hero1, hero2, hero3, hero4];
  const [currentHero, setCurrentHero] = useState(0);

  // Hero slideshow
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentHero((prev) => (prev + 1) % heroImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // Scroll animation for content
  useEffect(() => {
    const animateElements = document.querySelectorAll(
      ".equipment-content p, .equipment-content h2, .equipment-list li"
    );

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("active");
          }
        });
      },
      { threshold: 0.2 }
    );

    animateElements.forEach((el) => observer.observe(el));
    return () => animateElements.forEach((el) => observer.unobserve(el));
  }, []);

  return (
    <div className="equipment-container">

      {/* HERO SECTION */}
      <div
        className="equipment-hero"
        style={{ backgroundImage: `url(${heroImages[currentHero]})` }}
      >
        <div className="hero-overlay">
          <h1>TMC Trucks & Equipment</h1>
          <p>BLACK & CHROME. WE WEAR IT WELL.</p>
          <Link to="/join/apply" className="hero-apply-btn">
            Apply Now
          </Link>
        </div>
      </div>

      {/* INTRO CONTENT */}
      <div className="equipment-content">
        <p className="intro-text">
          Mention TMC and everyone immediately recognizes us for our immaculate,
          well-maintained fleet. Excellence has been non-negotiable â€” no shortcuts, no exceptions.
        </p>

        <h2>Driver Comfort Comes First</h2>
        <ul className="equipment-list">
          <li>Leather air-ride seats with lumbar controls</li>
          <li>Power heated mirrors</li>
          <li>CB radios</li>
          <li>Premium sound system with subwoofer (Sirius compatible)</li>
          <li>Collision & Roll Stability Safety Systems</li>
          <li>Manual and Automatic transmissions</li>
          <li>Disc brakes (front on all, full disc on newer models)</li>
          <li>Trimble Communication System with electronic logs</li>
          <li>Espar bunk heaters & LED interior lighting</li>
          <li>Anti-theft system</li>
          <li>Built-in power inverter with A/C outlets</li>
          <li>Flat-screen TVs (DIRECTV available)</li>
          <li>Double bunk with bunk controls</li>
          <li>Shore power</li>
          <li className="highlight">Company-paid truck & trailer wash every week</li>
        </ul>
      </div>

      {/* OPTIONAL IMAGE GALLERY */}
      {/*
      <div className="equipment-gallery">
        <h2>Our Fleet</h2>
        <div className="gallery-grid">
          {[truck1, truck2].map((img, idx) => (
            <div className="gallery-item" key={idx}>
              <img src={img} alt={`TMC Truck ${idx + 1}`} />
            </div>
          ))}
        </div>
      </div>
      */}
    </div>
  );
}

export default Equipment;
