import { useState, useRef, useEffect } from "react";
import Lottie from "lottie-react";
import "../../styles/ApplyToday.css";
import characterAnimation from "../../assets/animations/character.json";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("Hi üëã I‚Äôll help you fill this form.");
  const lottieRef = useRef();

  const requiredFields = [
    "firstName","lastName","ssn","dob","license",
    "address1","city","state","zip",
    "primaryPhone","email","confirmEmail"
  ];

  const completedCount = requiredFields.filter(
    (f) => form[f] && form[f].toString().trim() !== ""
  ).length;

  const totalRequired = requiredFields.length;

  const fieldHints = {
    firstName: "Enter your legal first name.",
    lastName: "Enter your family name.",
    ssn: "Your SSN/SIN is required for verification.",
    dob: "Your date of birth.",
    address1: "Street address where you live.",
    city: "City name.",
    state: "State or province.",
    zip: "Postal / ZIP code.",
    primaryPhone: "Your active phone number.",
    email: "Your primary email address.",
    confirmEmail: "Re-enter email to confirm."
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === "checkbox" ? checked : value });

    if (fieldHints[name]) {
      setHint(fieldHints[name]);
      lottieRef.current?.playSegments([0, 40], true);
    }

    if (value && value.length > 2) {
      lottieRef.current?.playSegments([41, 80], true);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      setMessage("‚ùå Emails do not match");
      setHint("Emails must match exactly.");
      lottieRef.current?.playSegments([120, 150], true);
      return;
    }

    if (!form.consent) {
      setMessage("‚ùå Consent is required");
      setHint("Please give consent to continue.");
      lottieRef.current?.playSegments([120, 150], true);
      return;
    }

    setMessage("üéâ Application submitted successfully!");
    setHint("Great job! Your application is submitted.");
    lottieRef.current?.playSegments([151, 200], true);
  };

  return (
    <div className="apply-container">
      <h1>Apply Today ‚Äì Join the RGM Family</h1>
      <p>Please complete all required fields carefully.</p>

      {/* ASSISTANT */}
      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        <div className="speech-bubble">
          {hint}
          <div className="progress-text">
            {completedCount}/{totalRequired} completed
          </div>
        </div>
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>
        <div className="section-card full">
          <h3>Personal Information</h3>
          <input name="firstName" placeholder="First Name *" value={form.firstName} onChange={handleChange} required />
          <input name="middleName" placeholder="Middle Name" value={form.middleName} onChange={handleChange} />
          <input name="lastName" placeholder="Last Name *" value={form.lastName} onChange={handleChange} required />
          <input name="ssn" placeholder="SSN / SIN *" value={form.ssn} onChange={handleChange} required />
          <input type="date" name="dob" value={form.dob} onChange={handleChange} required />
          <input name="license" placeholder="Driver License *" value={form.license} onChange={handleChange} required />
        </div>

        <div className="section-card full">
          <h3>Address</h3>
          <input name="address1" placeholder="Street Address *" value={form.address1} onChange={handleChange} required />
          <input name="address2" placeholder="Street Address Line 2" value={form.address2} onChange={handleChange} />
          <input name="city" placeholder="City *" value={form.city} onChange={handleChange} required />
          <input name="state" placeholder="State *" value={form.state} onChange={handleChange} required />
          <input name="zip" placeholder="ZIP Code *" value={form.zip} onChange={handleChange} required />
        </div>

        <div className="section-card full">
          <h3>Contact</h3>
          <input name="primaryPhone" placeholder="Phone *" value={form.primaryPhone} onChange={handleChange} required />
          <input name="email" type="email" placeholder="Email *" value={form.email} onChange={handleChange} required />
          <input name="confirmEmail" type="email" placeholder="Confirm Email *" value={form.confirmEmail} onChange={handleChange} required />

          <label className="checkbox-container">
            <input type="checkbox" name="consent" checked={form.consent} onChange={handleChange} />
            I consent to communication
          </label>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn full">Submit Application</button>
      </form>
    </div>
  );
}

export default ApplyToday;
