import React, { useState, useEffect } from "react";
import "./Gallery.css";

/* AUTO LOAD IMAGES */
const imagesContext = require.context(
  "../assets/gallery/images",
  false,
  /\.(jpg|jpeg|png|webp)$/
);

const videosContext = require.context(
  "../assets/gallery/videos",
  false,
  /\.(mp4)$/
);

const images = imagesContext.keys().map((key, index) => ({
  src: imagesContext(key),
  category:
    index % 4 === 0
      ? "Fleet"
      : index % 4 === 1
      ? "Drivers"
      : index % 4 === 2
      ? "On Road"
      : "Warehouse",
}));

const videos = videosContext.keys().map((key) => ({
  src: videosContext(key),
  category: "Videos",
}));

const categories = ["All", "Fleet", "Drivers", "On Road", "Warehouse", "Videos"];

const Gallery = () => {
  const [active, setActive] = useState("All");
  const [heroIndex, setHeroIndex] = useState(0);

  /* HERO SLIDESHOW */
  useEffect(() => {
    const interval = setInterval(() => {
      setHeroIndex((prev) => (prev + 1) % images.length);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  const filtered =
    active === "All"
      ? [...images, ...videos]
      : active === "Videos"
      ? videos
      : images.filter((img) => img.category === active);

  return (
    <div className="gallery-page">
      {/* HERO SLIDESHOW */}
      <section
        className="gallery-hero"
        style={{ backgroundImage: `url(${images[heroIndex].src})` }}
      >
        <div className="gallery-hero-overlay">
          <h1>RGM Gallery</h1>
          <p>Moments from the road, fleet & family</p>
        </div>
      </section>

      {/* CATEGORY TABS */}
      <div className="gallery-tabs">
        {categories.map((cat) => (
          <button
            key={cat}
            className={active === cat ? "active" : ""}
            onClick={() => setActive(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* MASONRY GRID */}
      <div className="gallery-masonry">
        {filtered.map((item, i) => (
          <div className="gallery-item" key={i}>
            {item.src.endsWith(".mp4") ? (
              <video
                src={item.src}
                muted
                loop
                playsInline
                onMouseEnter={(e) => e.target.play()}
                onMouseLeave={(e) => e.target.pause()}
              />
            ) : (
              <img src={item.src} alt="RGM Gallery" />
            )}
            <div className="gallery-overlay">
              <span>{item.category}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Gallery;
