import React, { useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Careers.css";

// Import images properly
import gallery1 from "../assets/gallery/images/gallery1.jpg";
import gallery2 from "../assets/gallery/images/gallery2.jpg";
import gallery3 from "../assets/gallery/images/gallery3.jpg";
import gallery4 from "../assets/gallery/images/gallery4.jpg";
import gallery5 from "../assets/gallery/images/gallery5.jpg";

const images = [gallery1, gallery2, gallery3, gallery4, gallery5];

const positions = [
  { title: "Flatbed Truck Driver", description: "Drive our modern flatbed trucks, ensure safe delivery, and enjoy weekly company-paid truck washes." },
  { title: "Logistics Coordinator", description: "Manage shipments, coordinate with drivers and customers, and optimize delivery schedules." },
  { title: "Customer Support Specialist", description: "Provide excellent support to our drivers and clients, resolving inquiries quickly and efficiently." },
  { title: "Maintenance Technician", description: "Ensure trucks and trailers are well-maintained and safe, performing regular inspections and repairs." },
  { title: "Operations Manager", description: "Oversee day-to-day operations, implement best practices, and support company growth and efficiency." }
];

function Careers() {
  const sectionRefs = useRef([]);
  const [openIndex, setOpenIndex] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);

  // Scroll animations
  useEffect(() => {
    const sections = [...sectionRefs.current];
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) entry.target.classList.add("animate-in");
        });
      },
      { threshold: 0.1 }
    );
    sections.forEach(ref => ref && observer.observe(ref));
    return () => sections.forEach(ref => ref && observer.unobserve(ref));
  }, []);

  // Slideshow auto-slide
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide(prev => (prev + 1) % images.length);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  const setRefs = (el, index) => {
    sectionRefs.current[index] = el;
  };

  const togglePosition = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <div className="rgm-careers-container">

      {/* HERO WITH SLIDESHOW */}
      <section className="rgm-hero fade-slide" ref={(el) => setRefs(el, 0)}>
        <div className="hero-slideshow">
          {images.map((img, idx) => (
            <img
              key={idx}
              src={img}
              alt={`Gallery ${idx + 1}`}
              className={`slide ${currentSlide === idx ? "active" : ""}`}
            />
          ))}
        </div>
        <div className="hero-text">
          <h1>Careers at RGM Family</h1>
          <p>Join a company where you’re not just an employee — you’re part of the family.</p>
        </div>
      </section>

      {/* WHY WORK WITH US */}
      <section className="rgm-section fade-slide" ref={(el) => setRefs(el, 1)}>
        <div className="icon-bg family">&#x2764;&#xFE0F;</div>
        <h2>Why Join RGM Family?</h2>
        <ul>
          <li>Be part of a family-oriented, supportive work culture</li>
          <li>Competitive pay and performance-based incentives</li>
          <li>Growth opportunities in logistics, driving, and operations</li>
          <li>Employee ownership and retirement benefits</li>
          <li>Training and mentorship programs to help you excel</li>
        </ul>
      </section>

      {/* OPEN POSITIONS */}
      <section className="rgm-section fade-slide" ref={(el) => setRefs(el, 2)}>
        <div className="icon-bg jobs">&#x1F4BC;</div>
        <h2>Open Positions</h2>
        <div className="positions-grid">
          {positions.map((pos, idx) => (
            <div key={idx} className={`position-card ${openIndex === idx ? "open" : ""}`} onClick={() => togglePosition(idx)}>
              <h3>{pos.title}</h3>
              {openIndex === idx && <p className="position-desc">{pos.description}</p>}
            </div>
          ))}
        </div>
        <p>Don't see a position that fits? Reach out — we love connecting with driven individuals.</p>
      </section>

      {/* BENEFITS */}
      <section className="rgm-section fade-slide" ref={(el) => setRefs(el, 3)}>
        <div className="icon-bg benefits">&#x1F389;</div>
        <h2>Benefits & Perks</h2>
        <ul>
          <li>Health, dental, and vision insurance</li>
          <li>Paid vacation and holidays</li>
          <li>Employee Stock Ownership Plan (ESOP)</li>
          <li>Family Assistance Fund for employee support</li>
          <li>On-the-job training and career development</li>
        </ul>
      </section>

      {/* CTA */}
      <section className="rgm-section fade-slide rgm-cta" ref={(el) => setRefs(el, 4)}>
        <h2>Ready to Join the Family?</h2>
        <p>We’re always looking for talented, motivated individuals to grow with us.</p>
        <Link to="/join/apply" className="apply-btn">
          Apply Now
        </Link>
      </section>
    </div>
  );
}

export default Careers;
