import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../../styles/Truckload.css";

function Truckload() {
  // Hero slideshow (gallery1 to gallery3)
  const heroImages = [
    "/assets/gallery/images/gallery3.jpg",
    "/assets/gallery/images/gallery4.jpg",
    "/assets/gallery/images/gallery5.jpg",
    "/assets/gallery/images/gallery6.jpg",
    "/assets/gallery/images/gallery7.jpg",
  ];

  const mainImages = Array.from({ length: 16 }, (_, i) => `/assets/gallery/images/gallery${i + 6}.jpg`);

  const [heroIndex, setHeroIndex] = useState(0);
  const [mainIndex, setMainIndex] = useState(0);

  useEffect(() => {
    const heroInterval = setInterval(() => {
      setHeroIndex(prev => (prev + 1) % heroImages.length);
    }, 5000); // 5s for hero

    const mainInterval = setInterval(() => {
      setMainIndex(prev => (prev + 1) % mainImages.length);
    }, 4000); // 4s for main slideshow

    return () => {
      clearInterval(heroInterval);
      clearInterval(mainInterval);
    };
  }, []);

  return (
    <div className="rgm-truckload-page">

      {/* HERO with slideshow */}
      <section className="rgm-truckload-hero">
        <div
          className="hero-slideshow"
          style={{ backgroundImage: `url(${heroImages[heroIndex]})` }}
        />
        <div className="hero-text">
          <h1>Flatbed Truckload Solutions</h1>
          <p>Built on Trust ‚Ä¢ Safety ‚Ä¢ Reliable Flatbed Capacity</p>
        </div>
      </section>

      {/* INTRO */}
      <section className="rgm-truckload-intro">
        <p>
          <strong>RGM Truckload</strong> specializes in full flatbed shipments that require experienced drivers, proper securement, and dependable communication from pickup to delivery.
        </p>
      </section>

      {/* MAIN SLIDESHOW */}
      <section className="rgm-truckload-main">
        <h2>Our Truckload Operations in Action</h2>
        <div className="main-slideshow-frame">
          <img src={mainImages[mainIndex]} alt="Truckload Slideshow" />
        </div>
      </section>

      {/* KEY FEATURES */}
      <section className="rgm-truckload-features">
        <h2>Truckload Key Features</h2>
        <div className="features-grid">
          <div className="feature-card">üöõ Flatbed & Stepdeck Capacity</div>
          <div className="feature-card">üîí Securement-Focused Operations</div>
          <div className="feature-card">üß∞ Construction & Industrial Freight</div>
          <div className="feature-card">üìç Regional & Nationwide Coverage</div>
          <div className="feature-card">üì° Real-Time Load Updates</div>
          <div className="feature-card">ü§ù Trusted Owner-Operators</div>
        </div>
      </section>

      {/* CAPABILITIES */}
      <section className="rgm-truckload-capabilities">
        <h2>Truckload Capabilities</h2>
        <div className="capability-grid">
          <span>48‚Äô & 53‚Äô Flatbed Loads</span>
          <span>Step Deck & Conestoga</span>
          <span>Oversized & Legal Loads</span>
          <span>Construction Materials</span>
          <span>Machinery & Equipment</span>
          <span>Project-Based Freight</span>
        </div>
      </section>

      {/* WHY RGM */}
      <section className="rgm-truckload-why">
        <h2>Why RGM Truckload?</h2>
        <p>
          We may be a growing company, but our approach is simple: treat every load like it belongs to family.
        </p>
        <p>
          Clear communication, honest timelines, and safe execution are non-negotiable at RGM.
        </p>
      </section>

      {/* FOOTPRINT */}
      <section className="rgm-truckload-footprint">
        <h2>Our Reach</h2>
        <p>
          RGM provides flatbed truckload service across key U.S. corridors, supporting regional and long-haul freight with reliable capacity.
        </p>
      </section>

      {/* CTA */}
      <section className="rgm-truckload-cta">
        <h2>Ready to Move a Flatbed Load?</h2>
        <p>
          Contact RGM today for dependable flatbed truckload solutions built on trust and accountability.
        </p>

        <div className="cta-buttons">
          <Link to="/contact" className="cta-primary">Get A Quote</Link>
          <Link to="/contact" className="cta-secondary">Click to Call</Link>
        </div>
      </section>

    </div>
  );
}

export default Truckload;
