import React, { useState, useEffect } from "react";
import "./Gallery.css";
import Footer from "../components/Footer";

// Load Images
const imageContext = require.context(
  "../assets/gallery/images",
  false,
  /\.(png|jpe?g|webp)$/
);

// Load Videos
const videoContext = require.context(
  "../assets/gallery/videos",
  false,
  /\.(mp4|webm)$/
);

const images = imageContext.keys().map((key) => ({
  type: "image",
  src: imageContext(key),
}));

const videos = videoContext.keys().map((key) => ({
  type: "video",
  src: videoContext(key),
}));

const allMedia = [...images, ...videos];

const Gallery = () => {
  const [filter, setFilter] = useState("all");
  const [active, setActive] = useState(null);
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    setTimeout(() => setVisible(true), 300);
  }, []);

  const filteredMedia =
    filter === "all"
      ? allMedia
      : allMedia.filter((item) => item.type === filter);

  return (
    <>
      <section className={`gallery-hero ${visible ? "show" : ""}`}>
        <h1>RGM Gallery</h1>
        <p>Life on the road. Strength in motion. Family forever.</p>

        <div className="gallery-filters">
          <button
            className={filter === "all" ? "active" : ""}
            onClick={() => setFilter("all")}
          >
            All
          </button>
          <button
            className={filter === "image" ? "active" : ""}
            onClick={() => setFilter("image")}
          >
            Photos
          </button>
          <button
            className={filter === "video" ? "active" : ""}
            onClick={() => setFilter("video")}
          >
            Videos
          </button>
        </div>
      </section>

      <section className="gallery-grid">
        {filteredMedia.map((item, index) => (
          <div
            key={index}
            className="gallery-card"
            onClick={() => setActive(item)}
          >
            {item.type === "image" ? (
              <img src={item.src} alt="RGM Gallery" />
            ) : (
              <>
                <video src={item.src} muted />
                <span className="play-icon">â–¶</span>
              </>
            )}
            <div className="card-glow" />
          </div>
        ))}
      </section>

      {/* LIGHTBOX */}
      {active && (
        <div className="lightbox" onClick={() => setActive(null)}>
          <div className="lightbox-inner">
            {active.type === "image" ? (
              <img src={active.src} alt="Preview" />
            ) : (
              <video src={active.src} controls autoPlay />
            )}
          </div>
        </div>
      )}

      <Footer />
    </>
  );
};

export default Gallery;
