import { useState, useRef, useEffect } from "react";
import Lottie from "lottie-react";
import "../../styles/ApplyToday.css";
import characterAnimation from "../../assets/animations/character.json";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("");

  const lottieRef = useRef();

  // üîπ Helper: fields order
  const fieldOrder = [
    "firstName",
    "lastName",
    "ssn",
    "dob",
    "license",
    "address1",
    "city",
    "state",
    "zip",
    "primaryPhone",
    "email",
    "confirmEmail",
    "consent"
  ];

  const getNextField = () => {
    for (let field of fieldOrder) {
      if (!form[field] || (field === "consent" && !form.consent)) {
        return field;
      }
    }
    return null;
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    setForm({ ...form, [name]: updatedValue });

    // üé≠ FIELD-SPECIFIC FEEDBACK
    switch (name) {
      case "firstName":
        lottieRef.current?.playSegments([0, 30], true);
        setHint(value ? "Looks good! Next is Last Name." : "Please enter your first name.");
        break;

      case "lastName":
        lottieRef.current?.playSegments([31, 60], true);
        setHint(value ? "Great! Moving ahead." : "Last name is required.");
        break;

      case "email":
        if (!value.includes("@")) {
          lottieRef.current?.playSegments([121, 150], true);
          setHint("Hmm‚Ä¶ email looks incorrect.");
        } else {
          lottieRef.current?.playSegments([61, 90], true);
          setHint("Email looks good üëç");
        }
        break;

      case "confirmEmail":
        if (value !== form.email) {
          lottieRef.current?.playSegments([121, 150], true);
          setHint("Emails do not match.");
        } else {
          lottieRef.current?.playSegments([61, 90], true);
          setHint("Emails match perfectly!");
        }
        break;

      case "primaryPhone":
        if (value.length < 10) {
          lottieRef.current?.playSegments([121, 150], true);
          setHint("Phone number seems too short.");
        } else {
          lottieRef.current?.playSegments([91, 120], true);
          setHint("Nice! Phone number is valid.");
        }
        break;

      case "consent":
        if (checked) {
          lottieRef.current?.playSegments([151, 180], true);
          setHint("Awesome! You‚Äôre almost done üéâ");
        } else {
          setHint("Consent is required to proceed.");
        }
        break;

      default:
        lottieRef.current?.playSegments([0, 20], true);
        setHint(`You are filling ${name.replace(/([A-Z])/g, " $1")}`);
    }

    // üîπ Show what is left
    const next = getNextField();
    if (next) {
      setTimeout(() => {
        setHint((prev) => `${prev} | Next: ${next.replace(/([A-Z])/g, " $1")}`);
      }, 400);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      setMessage("‚ùå Emails do not match!");
      lottieRef.current?.playSegments([121, 150], true);
      setHint("Please fix the email mismatch.");
      return;
    }

    if (!form.consent) {
      setMessage("‚ùå Consent is required!");
      lottieRef.current?.playSegments([121, 150], true);
      setHint("Please check the consent box.");
      return;
    }

    try {
      setLoading(true);
      lottieRef.current?.playSegments([151, 180], true);
      setHint("Submitting your application‚Ä¶");

      const res = await fetch("http://localhost:5000/apply", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      if (!res.ok) throw new Error("Failed");

      setMessage("üéâ Application submitted successfully!");
      lottieRef.current?.playSegments([181, 210], true);
      setHint("Congratulations! We‚Äôll contact you soon.");

    } catch {
      setMessage("‚ùå Submission failed. Try again.");
      lottieRef.current?.playSegments([121, 150], true);
      setHint("Something went wrong. Please retry.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      <h1>Apply Today ‚Äì Join the RGM Family</h1>

      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        {hint && <div className="speech-bubble">{hint}</div>}
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>
        {/* YOUR FORM ‚Äì UNCHANGED */}
      </form>
    </div>
  );
}

export default ApplyToday;
