import { useState, useRef, useEffect } from "react";
import Lottie from "lottie-react";
import "../../styles/ApplyToday.css";
import characterAnimation from "../../assets/animations/character.json";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState(""); // Speech bubble hint
  const [eyePosition, setEyePosition] = useState({ x: 0, y: 0 });

  const lottieRef = useRef();
  const characterRef = useRef();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === "checkbox" ? checked : value });

    // Animate character based on current field
    switch (name) {
      case "firstName":
        lottieRef.current?.playSegments([0, 30], true); // Nod
        setHint("Great! Enter your first name.");
        break;
      case "lastName":
        lottieRef.current?.playSegments([31, 60], true); // Smile
        setHint("Nice! Now your last name.");
        break;
      case "email":
        lottieRef.current?.playSegments([61, 90], true); // Wink
        setHint("Double-check your email.");
        break;
      case "primaryPhone":
        lottieRef.current?.playSegments([91, 120], true); // Happy
        setHint("Phone number, please!");
        break;
      default:
        setHint("");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      setMessage("âŒ Emails do not match!");
      lottieRef.current?.playSegments([121, 150], true); // Shake head
      return;
    }

    if (!form.consent) {
      setMessage("âŒ Please give consent!");
      lottieRef.current?.playSegments([121, 150], true); // Shake head
      return;
    }

    try {
      setLoading(true);
      setMessage("");
      lottieRef.current?.playSegments([151, 180], true); // Wave

      const res = await fetch("http://localhost:5000/apply", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      if (!res.ok) throw new Error("Failed");

      setMessage("ðŸŽ‰ Application submitted successfully!");
      lottieRef.current?.playSegments([181, 210], true); // Celebrate

      setForm({
        firstName: "",
        middleName: "",
        lastName: "",
        suffix: "",
        ssn: "",
        dob: "",
        address1: "",
        address2: "",
        country: "United States",
        city: "",
        state: "",
        zip: "",
        residence3yrs: "",
        primaryPhone: "",
        cellPhone: "",
        email: "",
        confirmEmail: "",
        consent: false,
        license: ""
      });
    } catch (err) {
      setMessage("âŒ Something went wrong. Try again.");
      lottieRef.current?.playSegments([121, 150], true); // Shake head
    } finally {
      setLoading(false);
    }
  };

  // Eye follow cursor
  useEffect(() => {
    const handleMouseMove = (e) => {
      if (!characterRef.current) return;
      const rect = characterRef.current.getBoundingClientRect();
      const x = ((e.clientX - rect.left) / rect.width - 0.5) * 20; // max 10px offset
      const y = ((e.clientY - rect.top) / rect.height - 0.5) * 10;
      setEyePosition({ x, y });
    };
    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  return (
    <div className="apply-container">
      <h1>Apply Today â€“ Join the RGM Family</h1>

      <p>
        Thank you for your interest in <strong>RGM Family</strong>. Please
        complete the application below. Incomplete applications may delay
        processing.
      </p>

      {/* CHARACTER AT BOTTOM-RIGHT */}
      <div className="character-wrapper" ref={characterRef}>
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        <div
          className="character-eyes"
          style={{ transform: `translate(${eyePosition.x}px, ${eyePosition.y}px)` }}
        />
        {hint && <div className="speech-bubble">{hint}</div>}
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>
        {/* Your form sections remain unchanged */}
      </form>
    </div>
  );
}

export default ApplyToday;
