import React, { useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import "../../styles/EmployeeOwnership.css";

/* ‚úÖ IMPORT IMAGES PROPERLY */
import g3 from "../../assets/gallery/images/gallery3.jpg";
import g5 from "../../assets/gallery/images/gallery5.jpg";
import g7 from "../../assets/gallery/images/gallery7.jpg";
import g9 from "../../assets/gallery/images/gallery9.jpg";
import g11 from "../../assets/gallery/images/gallery11.jpg";
import g13 from "../../assets/gallery/images/gallery13.jpg";
import g15 from "../../assets/gallery/images/gallery15.jpg";
import g18 from "../../assets/gallery/images/gallery18.jpg";

import g12 from "../../assets/gallery/images/gallery12.jpg";
import g14 from "../../assets/gallery/images/gallery14.jpg";
import g16 from "../../assets/gallery/images/gallery16.jpg";

function EmployeeOwnership() {
  const featureRefs = useRef([]);
  const heroRef = useRef(null);
  const ctaRef = useRef(null);

  /* HERO SLIDES */
  const heroImages = [g3, g5, g7, g9, g11, g13, g15, g18];
  const [heroIndex, setHeroIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setHeroIndex((prev) => (prev + 1) % heroImages.length);
    }, 4500);
    return () => clearInterval(interval);
  }, []);

  /* SCROLL ANIMATIONS */
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry, i) => {
          if (entry.isIntersecting) {
            entry.target.style.transitionDelay = `${i * 0.15}s`;
            entry.target.classList.add("animate-in");
          }
        });
      },
      { threshold: 0.15 }
    );

    featureRefs.current.forEach((el) => el && observer.observe(el));
    heroRef.current && observer.observe(heroRef.current);
    ctaRef.current && observer.observe(ctaRef.current);

    return () => observer.disconnect();
  }, []);

  const setRefs = (el, i) => (featureRefs.current[i] = el);

  return (
    <div className="eo-container">

      {/* HERO */}
      <section className="eo-hero fade-slide" ref={heroRef}>
        <div
          className="eo-hero-bg"
          style={{ backgroundImage: `url(${heroImages[heroIndex]})` }}
        />
        <div className="eo-hero-overlay" />

        <div className="eo-hero-content">
          <h1>Employee Ownership at RGM Logistics</h1>
          <p>Built by People. Strengthened by Trust. Grown Together.</p>
        </div>
      </section>

      {/* INTRO */}
      <section className="eo-intro fade-slide">
        <p>
          At <strong>RGM Logistics</strong>, ownership means pride, accountability,
          and shared success. This isn‚Äôt a job ‚Äî it‚Äôs a future we build together.
        </p>
      </section>

      {/* CREATIVE MAIN SECTION */}
      <section className="eo-sections">

        <div className="eo-story">
          <h2>Ownership Is a Mindset</h2>
          <p>
            We believe people perform best when they feel valued.  
            Every mile driven, every load delivered, every decision made ‚Äî
            it all contributes to something bigger.
          </p>
        </div>

        {/* IMAGE MOSAIC */}
        <div className="eo-image-mosaic">
          <img src={g12} alt="RGM culture" />
          <img src={g14} alt="Team work" />
          <img src={g16} alt="Growth" />
        </div>

        {/* FEATURES */}
        <div className="eo-feature" ref={(el) => setRefs(el, 0)}>
          <h3>üîí Stability & Security</h3>
          <p>
            Long-term vision. Long-term people. We grow slow, strong, and together.
          </p>
        </div>

        <div className="eo-feature" ref={(el) => setRefs(el, 1)}>
          <h3>ü§ù One Team Culture</h3>
          <p>
            No hierarchy of respect ‚Äî every role matters and every voice counts.
          </p>
        </div>

        <div className="eo-feature" ref={(el) => setRefs(el, 2)}>
          <h3>üìà Growth Beyond Pay</h3>
          <p>
            Leadership paths, skill development, and long-term opportunity.
          </p>
        </div>

        <div className="eo-feature" ref={(el) => setRefs(el, 3)}>
          <h3>üöÄ Future Builders</h3>
          <p>
            As RGM expands, opportunities multiply ‚Äî internally first.
          </p>
        </div>

      </section>

      {/* CTA */}
      <section className="eo-cta fade-slide" ref={ctaRef}>
        <h2>The RGM Promise</h2>
        <p>
          You don‚Äôt work for RGM ‚Äî you grow with RGM.
        </p>
        <Link to="/join/apply" className="eo-apply-btn">
          Join the RGM Family
        </Link>
      </section>

    </div>
  );
}

export default EmployeeOwnership;
