import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";
import logo from "../assets/logo.png";

const Navbar = () => {
  const [active, setActive] = useState(null);

  const toggleMenu = (name) => {
    setActive(active === name ? null : name);
  };

  const showMenu = (name) => setActive(name);
  const hideMenu = () => setActive(null);

  return (
    <header>
      {/* TOP STRIP */}
      <div className="topbar">
        {/* LOGO + NAME */}
        <div className="logo-section">
          <img src={logo} alt="RGM Logo" className="logo-img" />
          <div className="logo-text">
            <h2>RGM Line Haul Inc.</h2>
          </div>
        </div>

        {/* RIGHT LINKS */}
        <div className="right-links">
          <Link to="/">Home</Link>
          <Link to="/careers">Careers</Link>
          <Link to="/gallery">Gallery</Link>
          <Link to="/contact">Contact</Link>

          {/* Apply Now (replaces Login) */}
          <Link className="black-btn" to="/join/apply">
            Apply Now
          </Link>
        </div>
      </div>

      {/* NAVBAR MENU */}
      <nav className="main-nav">
        <ul className="right-nav">

          {/* JOIN RGM FAMILY */}
          <li
            onMouseEnter={() => showMenu("join")}
            onMouseLeave={hideMenu}
          >
            <span onClick={() => toggleMenu("join")}>
              Join the RGM family ▾
            </span>

            {active === "join" && (
              <div className="dropdown">
                <Link to="/join/apply">Apply Today</Link>
                <Link to="/join/inexperienced">Inexperienced Class A</Link>
                <Link to="/join/professional">Professional Drivers</Link>
                <Link to="/join/experienced">Experienced Class A Drivers</Link>
                <Link to="/join/openings">Current Openings</Link>
                <Link to="/join/pay">Pay</Link>
                <Link to="/join/equipment">Equipment</Link>
                <Link to="/join/benefits">Benefits</Link>
                <Link to="/join/cdl-schools">CDL Schools</Link>
              </div>
            )}
          </li>

          {/* TRANSPORTATION SOLUTIONS */}
          <li
            onMouseEnter={() => showMenu("transport")}
            onMouseLeave={hideMenu}
          >
            <span onClick={() => toggleMenu("transport")}>
              Transportation Solutions ▾
            </span>

            {active === "transport" && (
              <div className="dropdown">
                <Link to="/solutions">Solutions</Link>
                <Link to="/solutions/why-rgm">Why RGM Family</Link>
              </div>
            )}
          </li>

          {/* RGM LOGISTICS */}
          <li
            onMouseEnter={() => showMenu("logistics")}
            onMouseLeave={hideMenu}
          >
            <span onClick={() => toggleMenu("logistics")}>
              RGM Logistics ▾
            </span>

            {active === "logistics" && (
              <div className="dropdown">
                <Link to="/logistics/3pl">3PL</Link>
                <Link to="/logistics/ltl">LTL</Link>
                <Link to="/logistics/special-ops">Special OPS</Link>
                <Link to="/logistics/intermodal">Intermodal</Link>
                <Link to="/logistics/bulk">Bulk</Link>
                <Link to="/logistics/truckload">Truckload</Link>
                <Link to="/logistics/why-rgm">Why RGM Family</Link>
                <Link to="/logistics/who-we-are">Who We Are</Link>
              </div>
            )}
          </li>

          {/* OUR COMPANY */}
          <li
            onMouseEnter={() => showMenu("company")}
            onMouseLeave={hideMenu}
          >
            <span onClick={() => toggleMenu("company")}>
              Our Company ▾
            </span>

            {active === "company" && (
              <div className="dropdown">
                <Link to="/company/employee-ownership">
                  Employee Ownership
                </Link>
                <Link to="/company/benefits">
                  Benefits
                </Link>
                <Link to="/company/responsibility">
                  Corporate Responsibility
                </Link>
                <Link to="/company/founder">
                  About RGM Family Founder
                </Link>
              </div>
            )}
          </li>

        </ul>
      </nav>
    </header>
  );
};

export default Navbar;
