import React from "react";
import "./Home.css";
import heroImage from "../assets/hero-trucks.jpg"; // put your hero image here

const Home = () => {
  return (
    <div className="home">

      {/* HERO SECTION */}
      <section
        className="hero"
        style={{ backgroundImage: `url(${heroImage})` }}
      >
        <h1>A FLATBED TRUCKING COMPANY WITH CHARACTER.</h1>

        <div className="hero-btns">
          <button className="white-btn">APPLY TODAY</button>
          <button className="red-btn">SHIP WITH MELTON</button>
          <button className="red-btn">DRIVE FOR MELTON</button>
        </div>
      </section>

      {/* WHY CHOOSE SECTION */}
      <section className="why">
        <h2>CHOOSE MELTON AS YOUR GO-TO FLATBED TRUCKING COMPANY?</h2>

        <p className="why-text">
          Melton Truck Linesâ€™ dedication to customer satisfaction has set us apart from other flatbed truck companies.
          After 70 years, we continue to remain one of the top flatbed trucking companies in North America.
        </p>

        <div className="stats">
          <div><h3>1486</h3><p>Tractors</p></div>
          <div><h3>2400</h3><p>Trailers</p></div>
          <div><h3>5000+</h3><p>Satisfied Customers</p></div>
        </div>
      </section>
    </div>
  );
};

export default Home;
