import React, { useState, useEffect } from "react";
import "../../styles/InexperiencedClassA.css";
import { Link } from "react-router-dom";

/* HERO IMAGES */
const heroImages = [
  require("../../assets/images/hero/hero3.jpg"),
  require("../../assets/images/hero/hero4.jpg"),
  require("../../assets/gallery/images/gallery3.jpg"),
  require("../../assets/gallery/images/gallery4.jpg"),
];

function InexperiencedClassA() {
  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % heroImages.length);
    }, 5000); // slower for more effect
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="ica-container">

      {/* HERO SECTION */}
      <section
        className="ica-hero"
        style={{ backgroundImage: `url(${heroImages[currentSlide]})` }}
      >
        <div className="hero-overlay">
          <h1 className="hero-text">
            Inexperienced <span className="hero-color">Class A</span> Drivers
          </h1>
          <p className="hero-punchline">Drive for RGM Family â€“ Start Your Journey!</p>
          <Link to="/join/apply" className="apply-btn-link hero-apply">
            Apply Today
          </Link>
        </div>
      </section>

      {/* Main Content Section */}
      <div className="ica-content">

        <p className="highlight animate-on-scroll">
          RECENT DRIVING SCHOOL GRADUATE OR LIMITED CLASS A EXPERIENCE? No problem.
        </p>
        <p className="animate-on-scroll">
          RGM Family offers an orientation and flatbed training program just for you! 
          We've been training apprentice drivers.
        </p>

        <h2 className="animate-on-scroll">Orientation & Training</h2>
        <p className="animate-on-scroll">
          Orientation is two weeks at one of our training facilities located in Des Moines, Iowa or Columbia, South Carolina. 
          Orientation is followed by five weeks over-the-road (OTR) with a Driver Trainer 
          (4 weeks if you have an Automatic Transmission restriction on your CDL). 
          Home time is important to us, so we pair you with a trainer that lives within 150 miles of your home.
        </p>

        <ul>
          <li className="animate-on-scroll">Your first 7 days (Mon-Sun) is classroom instruction and hands-on load securement training.</li>
          <li className="animate-on-scroll">Your remaining 5 days (Mon-Fri) are equipment familiarization and OTR training.</li>
          <li className="animate-on-scroll">Apprentice drivers are paid $650/week during training.</li>
        </ul>

        <h2 className="animate-on-scroll">Why RGM Family?</h2>
        <ul>
          <li className="animate-on-scroll">Weekend Home Time</li>
          <li className="animate-on-scroll">Industry-Leading Pay (first-year drivers can earn $100,000+/year)</li>
          <li className="animate-on-scroll">Top Quality Equipment</li>
          <li className="animate-on-scroll">Flatbed Training with Safety Experts</li>
        </ul>

        <h2 className="animate-on-scroll">Employee-Owned</h2>
        <p className="animate-on-scroll">
          Very few companies in the U.S. are Employee-Owned. RGM Family is proud to be one of them.
        </p>

        <h2 className="animate-on-scroll">Orientation Facilities</h2>
        <p className="animate-on-scroll">Training facilities in Des Moines, Iowa and Columbia, South Carolina.</p>

        {/* Apply Today Button Again */}
        <div className="bottom-apply animate-on-scroll">
          <Link to="/join/apply" className="apply-btn-link">
            Apply Today
          </Link>
        </div>
      </div>
    </div>
  );
}

export default InexperiencedClassA;
