import { useState, useRef, useEffect } from "react";
import "../../styles/ApplyToday.css";
import Lottie from "lottie-react";
import characterAnimation from "../../assets/animations/character.json";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("Fill the form carefully âœï¸");
  const [animationState, setAnimationState] = useState("idle"); // idle, wave, error, celebrate
  const lottieRef = useRef();

  // Handle eyes follow cursor
  useEffect(() => {
    const handleMouseMove = (e) => {
      const character = document.querySelector(".character-container lottie-player, canvas");
      if (character) {
        const rect = character.getBoundingClientRect();
        const x = (e.clientX - rect.left) / rect.width - 0.5;
        const y = (e.clientY - rect.top) / rect.height - 0.5;
        character.style.transform = `rotateX(${-y * 10}deg) rotateY(${x * 10}deg)`;
      }
    };
    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === "checkbox" ? checked : value });
    setHint(`Typing ${name.replace(/([A-Z])/g, " $1")}...`);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      setAnimationState("error");
      setHint("Emails do not match âŒ");
      return;
    }

    if (!form.consent) {
      setAnimationState("error");
      setHint("Consent is required âŒ");
      return;
    }

    try {
      setLoading(true);
      setAnimationState("celebrate");
      setHint("Submitting your application ðŸŽ‰");

      const res = await fetch("http://localhost:5000/apply", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      if (!res.ok) throw new Error();

      setMessage(
        "âœ… Application submitted successfully! Our team will contact you shortly."
      );

      setForm({
        firstName: "",
        middleName: "",
        lastName: "",
        suffix: "",
        ssn: "",
        dob: "",
        address1: "",
        address2: "",
        country: "United States",
        city: "",
        state: "",
        zip: "",
        residence3yrs: "",
        primaryPhone: "",
        cellPhone: "",
        email: "",
        confirmEmail: "",
        consent: false,
        license: ""
      });

      setHint("Application submitted successfully ðŸŽ‰");
    } catch {
      setAnimationState("error");
      setHint("Submission failed âŒ");
    } finally {
      setLoading(false);
      setTimeout(() => setAnimationState("idle"), 2000);
    }
  };

  return (
    <div className="apply-container">
      <h1>Apply Today â€“ Join the RGM Family</h1>
      <p>
        Thank you for your interest in <strong>RGM Family</strong>. Please complete the application below. Incomplete applications may delay processing.
      </p>

      <form className="apply-form" onSubmit={handleSubmit}>
        <div className="section-card full">
          <h3>Personal Information</h3>
          <input name="firstName" placeholder="First Name *" required value={form.firstName} onChange={handleChange} />
          <input name="middleName" placeholder="Middle Name" value={form.middleName} onChange={handleChange} />
          <input name="lastName" placeholder="Last Name *" required value={form.lastName} onChange={handleChange} />
          <input name="suffix" placeholder="Suffix" value={form.suffix} onChange={handleChange} />
          <input name="ssn" placeholder="SSN / SIN *" required value={form.ssn} onChange={handleChange} />
          <input type="date" name="dob" required value={form.dob} onChange={handleChange} />
          <input name="license" placeholder="Driver License Number *" required value={form.license} onChange={handleChange} />
        </div>

        <div className="section-card full">
          <h3>Address</h3>
          <input name="address1" placeholder="Street Address Line 1 *" required value={form.address1} onChange={handleChange} />
          <input name="address2" placeholder="Street Address Line 2" value={form.address2} onChange={handleChange} />
          <input name="city" placeholder="City *" required value={form.city} onChange={handleChange} />
          <input name="state" placeholder="State / Province *" required value={form.state} onChange={handleChange} />
          <input name="zip" placeholder="Zip / Postal Code *" required value={form.zip} onChange={handleChange} />
          <select name="residence3yrs" required value={form.residence3yrs} onChange={handleChange}>
            <option value="">Residence 3+ years?</option>
            <option value="Yes">Yes</option>
            <option value="No">No</option>
          </select>
        </div>

        <div className="section-card full">
          <h3>Contact</h3>
          <input name="primaryPhone" placeholder="Primary Phone *" required value={form.primaryPhone} onChange={handleChange} />
          <input name="cellPhone" placeholder="Cell Phone" value={form.cellPhone} onChange={handleChange} />
          <input name="email" type="email" placeholder="Email *" required value={form.email} onChange={handleChange} />
          <input name="confirmEmail" type="email" placeholder="Confirm Email *" required value={form.confirmEmail} onChange={handleChange} />

          <div className="checkbox-container">
            <label>
              <input type="checkbox" name="consent" checked={form.consent} onChange={handleChange} />
              I consent to receive communication and confirmation
            </label>
          </div>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button
          className="submit-btn full"
          type="submit"
          disabled={loading}
          onMouseEnter={() => setAnimationState("wave")}
          onMouseLeave={() => setAnimationState("idle")}
        >
          {loading ? "Submitting..." : "Submit Application & Request Meeting"}
        </button>
      </form>

      {/* CHARACTER */}
      <div className={`character-container ${animationState}`}>
        <Lottie
          lottieRef={lottieRef}
          animationData={characterAnimation}
          loop
          autoplay
        />
        <div className="character-hint">{hint}</div>
      </div>
    </div>
  );
}

export default ApplyToday;
