import React, { useEffect, useState, useRef } from "react";
import "../pages/Home.css";
import Footer from "../components/Footer";
import CountUp from "react-countup";
import { useNavigate } from "react-router-dom";

import hero1 from "../assets/images/hero/hero1.jpg";
import hero2 from "../assets/images/hero/hero2.jpg";
import hero3 from "../assets/images/hero/hero3.jpg";
import hero4 from "../assets/images/hero/hero4.jpg";
import hero5 from "../assets/images/hero/hero5.jpg";
import hero6 from "../assets/images/hero/hero6.jpg";
import hero7 from "../assets/images/hero/hero7.jpg";
import hero8 from "../assets/images/hero/hero8.jpg";
import hero9 from "../assets/images/hero/hero9.jpg";
import hero10 from "../assets/images/hero/hero10.jpg";

import CARB from "../assets/images/hero/CARB.jpeg";
import CTPAT from "../assets/images/hero/CTPAT.jpeg";

const heroImages = [
  hero1, hero2, hero3, hero4, hero5,
  hero6, hero7, hero8, hero9, hero10
];

const Home = () => {
  const navigate = useNavigate();
  const [current, setCurrent] = useState(0);
  const intervalRef = useRef(null);
  const counterRef = useRef(null);

  useEffect(() => {
    intervalRef.current = setInterval(() => {
      setCurrent((prev) => (prev + 1) % heroImages.length);
    }, 5000);
    return () => clearInterval(intervalRef.current);
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => entry.isIntersecting && entry.target.classList.add("visible"),
      { threshold: 0.4 }
    );
    if (counterRef.current) observer.observe(counterRef.current);
    return () => observer.disconnect();
  }, []);

  return (
    <div className="home">
      {/* HERO */}
      <section className="hero-section">
        {heroImages.map((img, index) => (
          <div
            key={index}
            className={`hero-slide ${index === current ? "active" : ""}`}
            style={{ backgroundImage: `url(${img})` }}
          />
        ))}

        <div className="hero-overlay">
          <div className="hero-content">
            <h1>Professionalism Accompanied by Safety</h1>
            <p className="hero-subtitle">
              More than trucking ‚Äî we are a family on the road.
            </p>

            <div className="hero-buttons">
              <button className="hero-btn primary" onClick={() => navigate("/solutions")}>
                Haul with RGM
              </button>
              <button className="hero-btn secondary" onClick={() => navigate("/join/apply")}>
                Join the RGM Family
              </button>
            </div>

            <div className="hero-dots">
              {heroImages.map((_, index) => (
                <span
                  key={index}
                  className={`dot ${index === current ? "active" : ""}`}
                  onClick={() => setCurrent(index)}
                />
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* WHY CHOOSE */}
      <section className="why-choose">
        <h2>Why Choose RGM?</h2>
        <p>
          RGM is built on trust, safety, and a family-first culture that delivers
          results across borders.
        </p>

        {/* COUNTER */}
        <div className="counter fade-in" ref={counterRef}>
          <h3><CountUp end={5000} duration={4} />+</h3>
          <p>Satisfied Customers</p>
          <div className="counter-bar"><div className="counter-bar-fill" /></div>
        </div>

        {/* CERTIFICATIONS */}
        <div className="cert-trust-strip">
          <h3 className="cert-heading">Certified. Compliant. Cross-Border Ready.</h3>

          <div className="cert-grid">
            <div className="cert-card">
              <img src={CTPAT} alt="C-TPAT Certified" />
              <h4>C-TPAT Certified</h4>
              <p>U.S. Customs Trade Partnership Against Terrorism</p>
            </div>

            <div className="cert-card">
              <img src={CARB} alt="CARB Certified" />
              <h4>CARB Certified</h4>
              <p>California Air Resources Board Compliance</p>
            </div>

            <div className="cert-card bonded">
              <div className="bonded-icon">üõ°Ô∏è üá®üá¶</div>
              <h4>Canadian Bonded Carrier</h4>
              <p>Secure cross-border freight movement</p>
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Home;
