import React, { useState } from "react";
import "./Gallery.css";

/* ===== IMAGES ===== */
import g1 from "../assets/gallery/images/gallery1.jpg";
import g2 from "../assets/gallery/images/gallery2.jpg";
import g3 from "../assets/gallery/images/gallery3.jpg";
import g4 from "../assets/gallery/images/gallery4.jpg";
import g5 from "../assets/gallery/images/gallery5.jpg";
import g6 from "../assets/gallery/images/gallery6.jpg";

/* ===== VIDEOS ===== */
import v1 from "../assets/gallery/videos/video1.mp4";
import v2 from "../assets/gallery/videos/video2.mp4";
import v3 from "../assets/gallery/videos/video3.mp4";
import v4 from "../assets/gallery/videos/video4.mp4";

const images = [
  { src: g1, category: "Fleet" },
  { src: g2, category: "Fleet" },
  { src: g3, category: "Road" },
  { src: g4, category: "Warehouse" },
  { src: g5, category: "Team" },
  { src: g6, category: "Road" },
];

const videos = [
  { src: v1, category: "Videos" },
  { src: v2, category: "Videos" },
  { src: v3, category: "Videos" },
  { src: v4, category: "Videos" },
];

const categories = ["All", "Fleet", "Road", "Warehouse", "Team", "Videos"];

const Gallery = () => {
  const [active, setActive] = useState("All");

  const filteredContent =
    active === "All"
      ? [...images, ...videos]
      : active === "Videos"
      ? videos
      : images.filter((img) => img.category === active);

  return (
    <div className="rgm-gallery">

      <h1 className="gallery-title">RGM Gallery</h1>
      <p className="gallery-subtitle">
        A glimpse of our fleet, people, and journey on the road
      </p>

      {/* FILTERS */}
      <div className="gallery-tabs">
        {categories.map((cat) => (
          <button
            key={cat}
            className={active === cat ? "active" : ""}
            onClick={() => setActive(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* GRID */}
      <div className="gallery-grid">
        {filteredContent.map((item, index) => (
          <div key={index} className="gallery-card">
            {item.src.endsWith(".mp4") ? (
              <video
                src={item.src}
                muted
                loop
                playsInline
                onMouseEnter={(e) => e.target.play()}
                onMouseLeave={(e) => e.target.pause()}
              />
            ) : (
              <img src={item.src} alt="RGM Gallery" />
            )}
          </div>
        ))}
      </div>

    </div>
  );
};

export default Gallery;
