import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import "../../styles/Pay.css";

// Hero images
import hero1 from "../../assets/images/hero/hero1.jpg";
import hero2 from "../../assets/images/hero/hero2.jpg";
import hero3 from "../../assets/images/hero/hero3.jpg";
import hero4 from "../../assets/images/hero/hero4.jpg";

// Review screenshots
import review1 from "../../assets/reviews/review1.png";
import review2 from "../../assets/reviews/review2.png";

function Pay() {
  const heroImages = [hero1, hero2, hero3, hero4];

  const textReviews = [
    "RGM Family is amazing! The support from dispatch and trainers is top-notch. — John D.",
    "I love the pay structure here. Performance-based pay really motivates me to do my best. — Sarah M.",
    "The home time is great! I get weekends with my family. — Mike T.",
    "Employee-owned company! Feels good to be a part of a team that cares. — Laura K.",
    "Excellent equipment, always clean and reliable. — David P.",
    "Pay is always on time, complete transparency and no force dispatch with high level of accountability. — Navdeep Kataria"
  ];

  const imageReviews = [review1, review2];

  const [currentHero, setCurrentHero] = useState(0);
  const [currentText, setCurrentText] = useState(0);
  const [currentImage, setCurrentImage] = useState(0);

  /* ================= HERO SLIDESHOW ================= */
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentHero((prev) => (prev + 1) % heroImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  /* ================= REVIEWS SLIDES ================= */
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentText((prev) => (prev + 1) % textReviews.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % imageReviews.length);
    }, 6000);
    return () => clearInterval(timer);
  }, []);

  /* ================= REALISTIC STATS ================= */
  const finalStats = {
    satisfaction: 100,
    payIncrease: 95,
    retention: 97.7
  };

  const [stats, setStats] = useState({
    satisfaction: 0,
    payIncrease: 0,
    retention: 0
  });

  const statsRef = useRef(null);
  const animatedOnce = useRef(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !animatedOnce.current) {
          animatedOnce.current = true;
          animateStat("satisfaction", finalStats.satisfaction, 1200);
          animateStat("payIncrease", finalStats.payIncrease, 1200);
          animateStat("retention", finalStats.retention, 1400);
        }
      },
      { threshold: 0.4 }
    );

    if (statsRef.current) observer.observe(statsRef.current);
    return () => observer.disconnect();
  }, []);

  const animateStat = (key, target, duration) => {
    let current = 0;
    const increment = target / (duration / 16);

    const timer = setInterval(() => {
      current += increment;
      if (current >= target) {
        current = target;
        clearInterval(timer);
      }
      setStats((prev) => ({ ...prev, [key]: current }));
    }, 16);
  };

  return (
    <div className="pay-container">

      {/* HERO */}
      <section
        className="pay-hero"
        style={{ backgroundImage: `url(${heroImages[currentHero]})` }}
      >
        <div className="hero-overlay">
          <h1>Driver Pay at RGM Family</h1>
          <p>Performance-based pay built on trust and transparency</p>
          <Link to="/join/apply" className="hero-apply-btn">Apply Now</Link>
        </div>
      </section>

      {/* CONTENT */}
      <div className="pay-content">
        <h2>Maximize Your Earning Potential</h2>
        <p>
          At RGM Family, we reward commitment, consistency, and performance.
          Your hard work is recognized, respected, and paid fairly.
        </p>

        <h2>Additional Pay Opportunities</h2>
        <ul>
          <li>Tarp Pay</li>
          <li>Extra Stop Pay</li>
          <li>Detention Pay</li>
          <li>Over-Dimensional Load Pay</li>
        </ul>

        <h2>Our Driver Reality</h2>

        <div className="stats-real" ref={statsRef}>
          <div className="stat-real">
            <div className="stat-header">
              <span>Driver Satisfaction</span>
              <strong>{stats.satisfaction.toFixed(0)}%</strong>
            </div>
            <div className="bar">
              <div className="bar-fill" style={{ width: `${stats.satisfaction}%` }} />
            </div>
          </div>

          <div className="stat-real">
            <div className="stat-header">
              <span>Average Pay Increase</span>
              <strong>{stats.payIncrease.toFixed(0)}%</strong>
            </div>
            <div className="bar">
              <div className="bar-fill" style={{ width: `${stats.payIncrease}%` }} />
            </div>
          </div>

          <div className="stat-real">
            <div className="stat-header">
              <span>Retention Rate</span>
              <strong>{stats.retention.toFixed(1)}%</strong>
            </div>
            <div className="bar">
              <div className="bar-fill" style={{ width: `${stats.retention}%` }} />
            </div>
          </div>
        </div>
      </div>

      {/* CTA */}
      <div className="cta-box">
        <h2>Drive It Like You Own It.</h2>
        <Link to="/join/apply" className="apply-btn">Apply Now</Link>
      </div>

      {/* REVIEWS */}
      <div className="reviews-section">
        <h2>RGM Family Driver Reviews</h2>

        <div className="reviews-slider">
          <button onClick={() => setCurrentText((p) => (p - 1 + textReviews.length) % textReviews.length)}>&#10094;</button>
          <div className="review-text">{textReviews[currentText]}</div>
          <button onClick={() => setCurrentText((p) => (p + 1) % textReviews.length)}>&#10095;</button>
        </div>

        <div className="reviews-slider">
          <button onClick={() => setCurrentImage((p) => (p - 1 + imageReviews.length) % imageReviews.length)}>&#10094;</button>
          <img src={imageReviews[currentImage]} alt="Review" className="review-img" />
          <button onClick={() => setCurrentImage((p) => (p + 1) % imageReviews.length)}>&#10095;</button>
        </div>
      </div>
    </div>
  );
}

export default Pay;
