import { useState, useRef, useEffect } from "react";
import Lottie from "lottie-react";
import characterAnim from "../../assets/animations/character.json";
import "../../styles/ApplyToday.css";

function ApplyToday() {
  const lottieRef = useRef();
  const assistantRef = useRef();

  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("Hi üëã I‚Äôll help you fill this form!");

  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    confirmEmail: "",
    consent: false
  });

  /* üëÅ EYES FOLLOW CURSOR */
  useEffect(() => {
    const moveEyes = (e) => {
      const rect = assistantRef.current.getBoundingClientRect();
      const dx = e.clientX - (rect.left + rect.width / 2);
      const dy = e.clientY - (rect.top + rect.height / 2);

      assistantRef.current.style.transform =
        `translate(${dx * 0.02}px, ${dy * 0.02}px)`;
    };
    window.addEventListener("mousemove", moveEyes);
    return () => window.removeEventListener("mousemove", moveEyes);
  }, []);

  /* üó£ SMART HINTS */
  const handleFocus = (field) => {
    const hints = {
      firstName: "Enter your first name ‚úçÔ∏è",
      lastName: "Now your last name üßæ",
      email: "Make sure email is correct üìß",
      confirmEmail: "Emails must match ‚úÖ"
    };
    setHint(hints[field] || "Keep going üëç");
    lottieRef.current.goToAndPlay(0, true);
  };

  /* ‚ùå ERROR ANIMATION */
  const showError = (msg) => {
    setMessage(msg);
    assistantRef.current.classList.add("shake");
    setTimeout(() => assistantRef.current.classList.remove("shake"), 600);
  };

  /* üéâ SUCCESS */
  const celebrate = () => {
    setHint("üéâ Application Submitted!");
    lottieRef.current.setSpeed(1.6);
    lottieRef.current.play();
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      showError("‚ùå Emails do not match");
      return;
    }

    if (!form.consent) {
      showError("‚ùå Please give consent");
      return;
    }

    setMessage("‚úÖ Submitted successfully!");
    celebrate();
  };

  return (
    <div className="apply-page">
      {/* ü§ñ ASSISTANT */}
      <div className="assistant-wrapper" ref={assistantRef}>
        <Lottie
          lottieRef={lottieRef}
          animationData={characterAnim}
          loop
        />
        <div className="speech-bubble">{hint}</div>
      </div>

      {/* üìù FORM */}
      <form className="apply-form" onSubmit={handleSubmit}>
        <h2>Apply Today ‚Äì Join the RGM Family</h2>

        <input
          placeholder="First Name"
          onFocus={() => handleFocus("firstName")}
          onChange={(e) => setForm({ ...form, firstName: e.target.value })}
          required
        />

        <input
          placeholder="Last Name"
          onFocus={() => handleFocus("lastName")}
          onChange={(e) => setForm({ ...form, lastName: e.target.value })}
          required
        />

        <input
          type="email"
          placeholder="Email"
          onFocus={() => handleFocus("email")}
          onChange={(e) => setForm({ ...form, email: e.target.value })}
          required
        />

        <input
          type="email"
          placeholder="Confirm Email"
          onFocus={() => handleFocus("confirmEmail")}
          onChange={(e) =>
            setForm({ ...form, confirmEmail: e.target.value })
          }
          required
        />

        <label className="checkbox">
          <input
            type="checkbox"
            onChange={(e) => setForm({ ...form, consent: e.target.checked })}
          />
          I consent to communication
        </label>

        {message && <div className="form-message">{message}</div>}

        {/* ‚úã WAVE ON HOVER */}
        <button
          type="submit"
          className="submit-btn"
          onMouseEnter={() => lottieRef.current.goToAndPlay(20, true)}
        >
          Submit Application
        </button>
      </form>
    </div>
  );
}

export default ApplyToday;
