import React, { useState, useEffect } from "react";
import "../../styles/InexperiencedClassA.css";
import { Link } from "react-router-dom";

/* HERO IMAGES (choose your own images for this section) */
const heroImages = [
  require("../../assets/images/hero/hero3.jpg"),
  require("../../assets/images/hero/hero4.jpg"),
  require("../../assets/gallery/images/gallery3.jpg"),
  require("../../assets/gallery/images/gallery4.jpg"),
];

function InexperiencedClassA() {
  const [currentSlide, setCurrentSlide] = useState(0);

  /* HERO SLIDESHOW */
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % heroImages.length);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="ica-container">

      {/* HERO SECTION */}
      <section
        className="ica-hero"
        style={{ backgroundImage: `url(${heroImages[currentSlide]})` }}
      >
        <div className="hero-overlay">
          <h1 className="hero-text">Inexperienced Class A Drivers</h1>
          <p className="hero-punchline">Drive for RGM Family</p>
          <Link to="/join/apply" className="apply-btn-link hero-apply">
            Apply Today
          </Link>
        </div>
      </section>

      {/* Main Content Section */}
      <div className="ica-content">
        <p className="highlight">
          RECENT DRIVING SCHOOL GRADUATE OR LIMITED CLASS A EXPERIENCE? No problem.
        </p>
        <p>
          RGM Family offers an orientation and flatbed training program just for you! 
          We've been training apprentice drivers.
        </p>

        <h2>Orientation & Training</h2>
        <p>
          Orientation is two weeks at one of our training facilities located in Des Moines, Iowa or Columbia, South Carolina. 
          Orientation is followed by five weeks over-the-road (OTR) with a Driver Trainer 
          (4 weeks if you have an Automatic Transmission restriction on your CDL). 
          Home time is important to us, so we pair you with a trainer that lives within 150 miles of your home.
        </p>

        <ul>
          <li>Your first 7 days (Mon-Sun) is classroom instruction and hands-on load securement training.</li>
          <li>Your remaining 5 days (Mon-Fri) are equipment familiarization and OTR training.</li>
          <li>Apprentice drivers are paid $650/week during training.</li>
        </ul>

        <h2>Why RGM Family?</h2>
        <ul>
          <li>Weekend Home Time</li>
          <li>Industry-Leading Pay (first-year drivers can earn $100,000+/year)</li>
          <li>Top Quality Equipment</li>
          <li>Flatbed Training with Safety Experts</li>
        </ul>

        <h2>Employee-Owned</h2>
        <p>
          Very few companies in the U.S. are Employee-Owned. RGM Family is proud to be one of them.
        </p>

        <h2>Orientation Facilities</h2>
        <p>Training facilities in Des Moines, Iowa and Columbia, South Carolina.</p>

        {/* Apply Today Button Again */}
        <div className="bottom-apply">
          <Link to="/join/apply" className="apply-btn-link">
            Apply Today
          </Link>
        </div>
      </div>
    </div>
  );
}

export default InexperiencedClassA;
