import React, { useState } from "react";
import "./Gallery.css";
import Footer from "../components/Footer";

const Gallery = () => {
  const [activeItem, setActiveItem] = useState(null);

  // Images (50)
  const images = Array.from({ length: 50 }, (_, i) => ({
    type: "image",
    src: `/gallery/images/gallery${i + 1}.jpg`,
  }));

  // Videos (4)
  const videos = [
    { type: "video", src: "/gallery/videos/video1.mp4" },
    { type: "video", src: "/gallery/videos/video2.mp4" },
    { type: "video", src: "/gallery/videos/video3.mp4" },
    { type: "video", src: "/gallery/videos/video4.mp4" },
  ];

  const media = [...images, ...videos];

  return (
    <>
      <div className="gallery-page">
        <h1 className="gallery-title">RGM Gallery</h1>
        <p className="gallery-subtitle">
          Life on the road, our fleet, and our RGM family moments
        </p>

        <div className="gallery-grid">
          {media.map((item, index) => (
            <div
              key={index}
              className="gallery-item"
              onClick={() => setActiveItem(item)}
            >
              {item.type === "image" ? (
                <img src={item.src} alt="RGM Gallery" loading="lazy" />
              ) : (
                <video src={item.src} muted />
              )}
              <div className="gallery-overlay">View</div>
            </div>
          ))}
        </div>

        {/* LIGHTBOX */}
        {activeItem && (
          <div className="lightbox" onClick={() => setActiveItem(null)}>
            <div className="lightbox-content">
              {activeItem.type === "image" ? (
                <img src={activeItem.src} alt="Preview" />
              ) : (
                <video src={activeItem.src} controls autoPlay />
              )}
            </div>
          </div>
        )}
      </div>

      <Footer />
    </>
  );
};

export default Gallery;
