import React, { useEffect, useState } from "react";
import "./Home.css";
import { Link } from "react-router-dom";

function Home() {
  const [tractors, setTractors] = useState(0);
  const [trailers, setTrailers] = useState(0);
  const [customers, setCustomers] = useState(0);

  useEffect(() => {
    let t1 = setInterval(() => setTractors((prev) => (prev < 1396 ? prev + 7 : 1396)), 20);
    let t2 = setInterval(() => setTrailers((prev) => (prev < 2380 ? prev + 10 : 2380)), 20);
    let t3 = setInterval(() => setCustomers((prev) => (prev < 4500 ? prev + 20 : 4500)), 20);

    return () => { clearInterval(t1); clearInterval(t2); clearInterval(t3); };
  }, []);

  return (
    <div className="home">
      <div className="hero">
        <div className="buttons">
          <Link className="btn" to="/apply">Apply Today</Link>
          <Link className="btn" to="/ship">Ship with RGM</Link>
          <Link className="btn" to="/drive">Drive for RGM</Link>
        </div>
      </div>

      <div className="why">
        <h2>Why choose RGM as your go-to flatbed truck company?</h2>
        <p>
          RGM Line Haul Inc is committed to professionalism & safety.  
          With reliable flatbed services and a decade of trusted operations,  
          we deliver transportation with integrity.
        </p>
      </div>

      <div className="stats">
        <div className="stat-box">
          <h3>{tractors}</h3>
          <p>Tractors</p>
        </div>

        <div className="stat-box">
          <h3>{trailers}</h3>
          <p>Trailers</p>
        </div>

        <div className="stat-box">
          <h3>{customers}+</h3>
          <p>Satisfied Customers</p>
        </div>
      </div>
    </div>
  );
}

export default Home;
