import React, { useEffect, useState, useRef } from "react";
import "../pages/Home.css";
import Footer from "../components/Footer";
import CountUp from "react-countup";
import { useNavigate } from "react-router-dom";

import hero1 from "../assets/images/hero/hero1.jpg";
import hero2 from "../assets/images/hero/hero2.jpg";
import hero3 from "../assets/images/hero/hero3.jpg";
import hero4 from "../assets/images/hero/hero4.jpg";
import hero5 from "../assets/images/hero/hero5.jpg";
import hero6 from "../assets/images/hero/hero6.jpg";
import hero7 from "../assets/images/hero/hero7.jpg";
import hero8 from "../assets/images/hero/hero8.jpg";
import hero9 from "../assets/images/hero/hero9.jpg";
import hero10 from "../assets/images/hero/hero10.jpg";

const heroImages = [
  hero1, hero2, hero3, hero4, hero5,
  hero6, hero7, hero8, hero9, hero10
];

const Home = () => {
  const navigate = useNavigate();
  const [current, setCurrent] = useState(0);
  const intervalRef = useRef(null);
  const counterRefs = useRef([]);

  // Slideshow
  const startSlideShow = () => {
    intervalRef.current = setInterval(() => {
      setCurrent((prev) => (prev + 1) % heroImages.length);
    }, 5000);
  };
  const stopSlideShow = () => clearInterval(intervalRef.current);
  useEffect(() => { startSlideShow(); return () => stopSlideShow(); }, []);

  // Scroll Animation for counters
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("visible");
          }
        });
      },
      { threshold: 0.3 }
    );

    counterRefs.current.forEach((ref) => ref && observer.observe(ref));
    return () => counterRefs.current.forEach((ref) => ref && observer.unobserve(ref));
  }, []);

  return (
    <div className="home">
      {/* HERO SLIDESHOW */}
      <section className="hero-section">
        {heroImages.map((img, index) => (
          <div
            key={index}
            className={`hero-slide ${index === current ? "active" : ""}`}
            style={{ backgroundImage: `url(${img})` }}
          />
        ))}

        <div className="hero-overlay">
          <div className="hero-content">
            <h1>Professionalism Accompanied by Safety</h1>
            <p className="hero-subtitle">More than trucking â€” we are a family on the road.</p>

            <div className="hero-buttons">
              <button className="hero-btn primary" onClick={() => navigate("/solutions")}>
                Haul with RGM
              </button>
              <button className="hero-btn secondary" onClick={() => navigate("/join/apply")}>
                Join the RGM Family
              </button>
            </div>

            <div className="hero-dots">
              {heroImages.map((_, index) => (
                <span
                  key={index}
                  className={`dot ${index === current ? "active" : ""}`}
                  onClick={() => setCurrent(index)}
                />
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* WHY CHOOSE */}
      <section className="why-choose">
        <h2>Why Choose RGM?</h2>
        <p>RGM is built on trust, safety, and a family-first culture that delivers results for customers and drivers alike.</p>

        <div className="counters">
          {[
            { label: "Tractors", value: 1486 },
            { label: "Trailers", value: 2400 },
            { label: "Satisfied Customers", value: 5000 }
          ].map((item, idx) => (
            <div
              key={idx}
              className="counter fade-in"
              ref={(el) => (counterRefs.current[idx] = el)}
            >
              <h3><CountUp end={item.value} duration={4} /></h3>
              <p>{item.label}</p>
              <div className="counter-bar">
                <div className="counter-bar-fill" style={{ width: `${Math.min(item.value/50, 100)}%` }} />
              </div>
            </div>
          ))}
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Home;
