import React, { useEffect, useState } from "react";
import "./Home.css";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

function Home() {
  // COUNTER ANIMATION
  const [tractor, setTractor] = useState(0);
  const [trailer, setTrailer] = useState(0);
  const [customer, setCustomer] = useState(0);

  useEffect(() => {
    let t1 = setInterval(() => {
      setTractor((prev) => (prev < 1396 ? prev + 7 : 1396));
    }, 10);

    let t2 = setInterval(() => {
      setTrailer((prev) => (prev < 2380 ? prev + 10 : 2380));
    }, 10);

    let t3 = setInterval(() => {
      setCustomer((prev) => (prev < 4500 ? prev + 25 : 4500));
    }, 10);

    return () => {
      clearInterval(t1);
      clearInterval(t2);
      clearInterval(t3);
    };
  }, []);

  return (
    <>
      <Navbar />

      {/* HERO SECTION */}
      <div className="hero-section">
        <div className="hero-content">
          <h1>RGM LINE HAUL INC</h1>
          <p>Professionalism Accompanied With Safety</p>

          <div className="hero-buttons">
            <a href="/apply" className="btn-primary">Apply Today</a>
            <a href="/ship" className="btn-secondary">Ship With RGM</a>
            <a href="/drive" className="btn-primary">Drive For RGM</a>
          </div>
        </div>
      </div>

      {/* WHY CHOOSE SECTION */}
      <section className="why-section">
        <h2>Why Choose RGM as Your Go-To Flatbed Truck Company?</h2>
        <p>
          We prioritize safety, professionalism, and reliable deliveries across North America.
          RGM Line Haul Inc ensures top-tier equipment, experienced drivers, and advanced logistics solutions.
        </p>

        <div className="stats-container">
          <div className="stat-box">
            <h1>{tractor}</h1>
            <p>Tractors</p>
          </div>
          <div className="stat-box">
            <h1>{trailer}</h1>
            <p>Trailers</p>
          </div>
          <div className="stat-box">
            <h1>{customer}+</h1>
            <p>Satisfied Customers</p>
          </div>
        </div>
      </section>

      <Footer />
    </>
  );
}

export default Home;
