import React, { useEffect, useState } from "react";
import "./Gallery.css";

/* IMAGES */
const allImages = Array.from({ length: 50 }, (_, i) => ({
  src: require(`../assets/gallery/images/gallery${i + 1}.jpg`),
  category:
    i < 10
      ? "Fleet"
      : i < 20
      ? "Road"
      : i < 35
      ? "Warehouse"
      : "Team",
}));

/* VIDEOS */
const videos = Array.from({ length: 4 }, (_, i) => ({
  src: require(`../assets/gallery/videos/video${i + 1}.mp4`),
  category: "Videos",
}));

/* HERO */
const heroSlides = allImages.slice(0, 6);

const categories = ["All", "Fleet", "Road", "Warehouse", "Team", "Videos"];

const Gallery = () => {
  const [current, setCurrent] = useState(0);
  const [activeCategory, setActiveCategory] = useState("All");

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % heroSlides.length);
    }, 4500);
    return () => clearInterval(interval);
  }, []);

  const filteredImages =
    activeCategory === "All"
      ? allImages
      : allImages.filter((img) => img.category === activeCategory);

  return (
    <div className="gallery-page">

      {/* HERO */}
      <section className="gallery-hero">
        {heroSlides.map((img, index) => (
          <div
            key={index}
            className={`gallery-hero-slide ${
              index === current ? "active" : ""
            }`}
            style={{ backgroundImage: `url(${img.src})` }}
          />
        ))}
        <div className="gallery-hero-overlay">
          <h1>Life at RGM</h1>
          <p>Driven by people. Powered by purpose.</p>
        </div>
      </section>

      {/* FILTER TABS */}
      <div className="gallery-filters">
        {categories.map((cat) => (
          <button
            key={cat}
            className={activeCategory === cat ? "active" : ""}
            onClick={() => setActiveCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* GALLERY GRID */}
      <section className="gallery-grid">
        {(activeCategory === "Videos" ? videos : filteredImages).map(
          (item, i) => (
            <div
              key={i}
              className="gallery-item"
              style={{ animationDelay: `${i * 0.06}s` }}
            >
              {item.src.includes(".mp4") ? (
                <video
                  src={item.src}
                  muted
                  loop
                  playsInline
                  onMouseEnter={(e) => e.target.play()}
                  onMouseLeave={(e) => e.target.pause()}
                />
              ) : (
                <img src={item.src} alt="RGM Gallery" />
              )}
            </div>
          )
        )}
      </section>
    </div>
  );
};

export default Gallery;
