import { useState, useRef } from "react";
import Lottie from "lottie-react";
import "../../styles/ApplyToday.css";
import characterAnimation from "../../assets/animations/character.json";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [hint, setHint] = useState("üëã I‚Äôll guide you step by step.");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const lottieRef = useRef();

  const totalFields = 10;
  const filledFields = Object.values(form).filter(Boolean).length;

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === "checkbox" ? checked : value;

    setForm({ ...form, [name]: val });

    // Assistant logic
    if (!val && type !== "checkbox") {
      lottieRef.current?.playSegments([120, 150], true);
      setHint(`‚ö†Ô∏è ${name.replace(/([A-Z])/g, " $1")} is required`);
      return;
    }

    if (name === "email" && !value.includes("@")) {
      lottieRef.current?.playSegments([120, 150], true);
      setHint("‚ö†Ô∏è Email format looks incorrect");
      return;
    }

    if (name === "confirmEmail" && value !== form.email) {
      lottieRef.current?.playSegments([120, 150], true);
      setHint("‚ö†Ô∏è Emails do not match");
      return;
    }

    // Success reaction
    lottieRef.current?.playSegments([30, 60], true);
    setHint(
      `‚úÖ ${name.replace(/([A-Z])/g, " $1")} looks good  
       (${filledFields}/${totalFields} completed)`
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      setMessage("‚ùå Emails do not match");
      lottieRef.current?.playSegments([120, 150], true);
      return;
    }

    if (!form.consent) {
      setMessage("‚ùå Please give consent");
      lottieRef.current?.playSegments([120, 150], true);
      return;
    }

    try {
      setLoading(true);
      setMessage("");
      lottieRef.current?.playSegments([160, 190], true);
      setHint("üöÄ Submitting your application...");

      await new Promise((r) => setTimeout(r, 1500));

      setMessage("üéâ Application submitted successfully!");
      setHint("üéä You‚Äôre officially done!");
      lottieRef.current?.playSegments([200, 240], true);
    } catch {
      setMessage("‚ùå Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      <h1>Apply Today ‚Äì Join the RGM Family</h1>

      <p>
        Thank you for your interest in <strong>RGM Family</strong>. Please
        complete the application below.
      </p>

      {/* Assistant */}
      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          lottieRef={lottieRef}
          loop
          className="character-animation"
        />
        <div className="speech-bubble">{hint}</div>
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>
        <div className="section-card full">
          <h3>Personal Information</h3>
          <input name="firstName" placeholder="First Name *" required value={form.firstName} onChange={handleChange} />
          <input name="middleName" placeholder="Middle Name" value={form.middleName} onChange={handleChange} />
          <input name="lastName" placeholder="Last Name *" required value={form.lastName} onChange={handleChange} />
          <input name="suffix" placeholder="Suffix" value={form.suffix} onChange={handleChange} />
          <input name="ssn" placeholder="SSN / SIN *" required value={form.ssn} onChange={handleChange} />
          <input type="date" name="dob" required value={form.dob} onChange={handleChange} />
          <input name="license" placeholder="Driver License Number *" required value={form.license} onChange={handleChange} />
        </div>

        <div className="section-card full">
          <h3>Address</h3>
          <input name="address1" placeholder="Street Address Line 1 *" required value={form.address1} onChange={handleChange} />
          <input name="address2" placeholder="Street Address Line 2" value={form.address2} onChange={handleChange} />
          <input name="city" placeholder="City *" required value={form.city} onChange={handleChange} />
          <input name="state" placeholder="State / Province *" required value={form.state} onChange={handleChange} />
          <input name="zip" placeholder="Zip / Postal Code *" required value={form.zip} onChange={handleChange} />
          <select name="residence3yrs" required value={form.residence3yrs} onChange={handleChange}>
            <option value="">Residence 3+ years?</option>
            <option value="Yes">Yes</option>
            <option value="No">No</option>
          </select>
        </div>

        <div className="section-card full">
          <h3>Contact</h3>
          <input name="primaryPhone" placeholder="Primary Phone *" required value={form.primaryPhone} onChange={handleChange} />
          <input name="cellPhone" placeholder="Cell Phone" value={form.cellPhone} onChange={handleChange} />
          <input name="email" type="email" placeholder="Email *" required value={form.email} onChange={handleChange} />
          <input name="confirmEmail" type="email" placeholder="Confirm Email *" required value={form.confirmEmail} onChange={handleChange} />

          <div className="checkbox-container">
            <input type="checkbox" name="consent" checked={form.consent} onChange={handleChange} />
            <span>I consent to receive communication</span>
          </div>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn full" type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit Application"}
        </button>
      </form>
    </div>
  );
}

export default ApplyToday;
