import React from "react";
import "./Home.css";
import bg from "../assets/images/truck-bg.jpg";

const Home = () => {
  return (
    <div className="home-container">

      {/* HERO SECTION */}
      <div className="hero" style={{ backgroundImage: `url(${bg})` }}>
        <h1>A FLATBED TRUCKING COMPANY WITH CHARACTER.</h1>

        <div className="hero-buttons">
          <button className="white-btn">APPLY TODAY</button>
          <button className="red-btn">SHIP WITH RGM</button>
          <button className="red-btn">DRIVE FOR RGM</button>
        </div>
      </div>

      {/* WHY CHOOSE SECTION */}
      <div className="why-section">
        <h2>WHY CHOOSE RGM AS YOUR GO-TO FLATBED TRUCKING COMPANY?</h2>

        <p>
          RGMâ€™s dedication to customer satisfaction and safety sets us apart in the flatbed trucking industry.
          Our trucks serve across the United States & Canada with top-level reliability and professionalism.
        </p>

        <div className="stats">
          <div><h3>1396</h3><p>Tractors</p></div>
          <div><h3>2380</h3><p>Trailers</p></div>
          <div><h3>4500+</h3><p>Satisfied Customers</p></div>
        </div>
      </div>

    </div>
  );
};

export default Home;
