import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../../styles/Pay.css";

// import your screenshots here
import review1 from "../../assets/reviews/review1.png";
import review2 from "../../assets/reviews/review2.png";

function Pay() {
  const reviews = [review1, review2];
  const [current, setCurrent] = useState(0);

  const nextReview = () => {
    setCurrent((prev) => (prev + 1) % reviews.length);
  };

  const prevReview = () => {
    setCurrent((prev) => (prev - 1 + reviews.length) % reviews.length);
  };

  return (
    <div className="pay-container">
      {/* HERO PAY SECTION */}
      <div className="pay-hero">
        <h1>Driver Pay at RGM Family</h1>
        <p>Maximize your earning potential with performance-based pay</p>
      </div>

      {/* CONTENT */}
      <div className="pay-content">
        <h2>Maximize Your Earning Potential</h2>
        <p>
          At RGM Family, we reward drivers for performance, not just miles.
          Our pay structure ensures your hard work reflects directly in
          your paycheck. Focus on driving — we’ll take care of the rest.
        </p>

        <h2>Additional Pay Opportunities</h2>
        <ul>
          <li>Tarp Pay</li>
          <li>Breakdown Pay</li>
          <li>Referral Bonuses</li>
          <li>Stop Pay</li>
          <li>Detention Pay</li>
          <li>Over-Dimensional Load Pay</li>
          <li>Vacation Pay</li>
          <li>Sign-On Bonuses</li>
        </ul>
      </div>

      {/* CTA BOX */}
      <div className="cta-box">
        <h2>Drive It Like You Own It.</h2>
        <Link to="/join/apply" className="apply-btn">
          Apply Now
        </Link>
      </div>

      {/* REVIEWS SLIDESHOW */}
      <div className="reviews-section">
        <h2>RGM Family Driver Reviews</h2>

        <div className="reviews-slider">
          <button className="review-btn left" onClick={prevReview}>
            &#10094;
          </button>
          <img src={reviews[current]} alt={`Review ${current + 1}`} />
          <button className="review-btn right" onClick={nextReview}>
            &#10095;
          </button>
        </div>

        <p className="review-ref">
          Reviews source:{" "}
          <a
            href="https://www.carriersource.io/carriers/rgm-line-haul-inc#reviews"
            target="_blank"
            rel="noreferrer"
          >
            CarrierSource
          </a>
        </p>
      </div>
    </div>
  );
}

export default Pay;
