import React from "react";
import "../pages/Home.css";
import heroTrucks from "../assets/images/hero-trucks.jpg";
import Footer from "../components/Footer";
import CountUp from "react-countup";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();

  return (
    <div className="home">
      {/* Hero Section */}
      <section
        className="hero-section"
        style={{ backgroundImage: `url(${heroTrucks})` }}
      >
        <div className="hero-overlay">
          <div className="hero-content">
            <h1>Professionalism Accompanied by Safety</h1>

            <div className="hero-buttons">
              {/* Haul with RGM */}
              <button
                className="hero-btn primary"
                onClick={() => navigate("/solutions")}
              >
                Haul with RGM
              </button>

              {/* Join RGM Family */}
              <button
                className="hero-btn secondary"
                onClick={() => navigate("/join/apply")}
              >
                Join the RGM Family
              </button>
            </div>

            <div className="scroll-down">&#x2193;</div>
          </div>
        </div>
      </section>

      {/* Why Choose Section */}
      <section className="why-choose">
        <h2>Why Choose RGM?</h2>
        <p>
          RGMâ€™s family-first atmosphere and dedication to safety and reliability
          make us a trusted transportation partner across North America.
        </p>

        <div className="counters">
          <div className="counter">
            <h3><CountUp end={1486} duration={4} /></h3>
            <p>Tractors</p>
          </div>
          <div className="counter">
            <h3><CountUp end={2400} duration={4} /></h3>
            <p>Trailers</p>
          </div>
          <div className="counter">
            <h3><CountUp end={5000} duration={4} /></h3>
            <p>Satisfied Customers</p>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Home;
