import React, { useEffect, useState } from "react";
import "./Gallery.css";

/* IMAGES */
const images = Array.from({ length: 50 }, (_, i) => ({
  src: require(`../assets/gallery/images/gallery${i + 1}.jpg`),
}));

/* VIDEOS */
const videos = Array.from({ length: 4 }, (_, i) => ({
  src: require(`../assets/gallery/videos/video${i + 1}.mp4`),
}));

/* HERO SLIDESHOW (TOP 6 IMAGES) */
const heroSlides = images.slice(0, 6);

const Gallery = () => {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % heroSlides.length);
    }, 4500);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="gallery-page">

      {/* HERO SLIDESHOW */}
      <section className="gallery-hero">
        {heroSlides.map((img, index) => (
          <div
            key={index}
            className={`gallery-hero-slide ${
              index === current ? "active" : ""
            }`}
            style={{ backgroundImage: `url(${img.src})` }}
          />
        ))}
        <div className="gallery-hero-overlay">
          <h1>Life at RGM</h1>
          <p>Every mile has a story</p>
        </div>
      </section>

      {/* GALLERY SECTIONS */}
      <section className="gallery-sections">

        <GallerySection
          title="Our Fleet"
          subtitle="Power & performance"
          items={images.slice(0, 10)}
        />

        <GallerySection
          title="On The Road"
          subtitle="Miles that matter"
          items={images.slice(10, 20)}
        />

        <GallerySection
          title="Warehouses"
          subtitle="Strength behind the scenes"
          items={images.slice(20, 35)}
        />

        <GallerySection
          title="Team & Culture"
          subtitle="Family that drives success"
          items={images.slice(35, 50)}
        />

        <GallerySection
          title="Videos"
          subtitle="RGM in motion"
          items={videos}
          isVideo
        />

      </section>
    </div>
  );
};

/* SECTION COMPONENT */
const GallerySection = ({ title, subtitle, items, isVideo }) => {
  return (
    <div className="gallery-section">
      <div className="section-header">
        <h2>{title}</h2>
        <p>{subtitle}</p>
      </div>

      <div className="masonry">
        {items.map((item, i) => (
          <div
            className="masonry-item"
            style={{ animationDelay: `${i * 0.08}s` }}
            key={i}
          >
            {isVideo ? (
              <>
                <video src={item.src} muted />
                <span className="play-icon">â–¶</span>
              </>
            ) : (
              <img src={item.src} alt={title} />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Gallery;
