import { useState, useRef } from "react";
import Lottie from "lottie-react";
import "../../styles/ApplyToday.css";
import characterAnimation from "../../assets/animations/character.json";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("");

  const lottieRef = useRef();

  const requiredFields = [
    "firstName",
    "lastName",
    "ssn",
    "dob",
    "license",
    "address1",
    "city",
    "state",
    "zip",
    "primaryPhone",
    "email",
    "confirmEmail"
  ];

  const remainingFields = () =>
    requiredFields.filter((f) => !form[f]);

  const playHappy = () => lottieRef.current?.playSegments([31, 60], true);
  const playExcited = () => lottieRef.current?.playSegments([151, 180], true);
  const playSad = () => lottieRef.current?.playSegments([121, 150], true);
  const playAttention = () => lottieRef.current?.playSegments([0, 20], true);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === "checkbox" ? checked : value;

    setForm({ ...form, [name]: val });

    playAttention();

    switch (name) {
      case "firstName":
        setHint(val ? "Nice first name üëç Next: Last Name" : "Please enter your first name");
        val ? playHappy() : playSad();
        break;

      case "lastName":
        setHint(val ? "Great! Keep going üöÄ" : "Last name is required");
        val ? playHappy() : playSad();
        break;

      case "email":
        if (!val.includes("@")) {
          setHint("That email doesn‚Äôt look right");
          playSad();
        } else {
          setHint("Email looks good üëç");
          playHappy();
        }
        break;

      case "confirmEmail":
        if (val !== form.email) {
          setHint("Emails do not match");
          playSad();
        } else {
          setHint("Emails match perfectly!");
          playHappy();
        }
        break;

      case "primaryPhone":
        if (val.length < 10) {
          setHint("Phone number seems too short");
          playSad();
        } else {
          setHint("Phone number looks valid üìû");
          playHappy();
        }
        break;

      case "consent":
        if (checked) {
          setHint("Awesome! You‚Äôre almost done üéâ");
          playExcited();
        } else {
          setHint("Consent is required to submit");
          playSad();
        }
        break;

      default:
        setHint(`You are filling ${name.replace(/([A-Z])/g, " $1")}`);
    }

    const left = remainingFields();
    if (left.length > 0) {
      setTimeout(() => {
        setHint(
          `Good progress! Left: ${left.length} field${left.length > 1 ? "s" : ""}`
        );
      }, 700);
    } else {
      setHint("Everything looks good! You can submit now üéØ");
      playExcited();
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      setMessage("‚ùå Emails do not match!");
      setHint("Please fix the email mismatch");
      playSad();
      return;
    }

    if (!form.consent) {
      setMessage("‚ùå Please give consent!");
      setHint("Consent is required before submitting");
      playSad();
      return;
    }

    try {
      setLoading(true);
      setHint("Submitting your application...");
      playExcited();

      const res = await fetch("http://localhost:5000/apply", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      if (!res.ok) throw new Error();

      setMessage("üéâ Application submitted successfully!");
      setHint("Congratulations! We‚Äôll contact you soon üòä");
      lottieRef.current?.playSegments([181, 210], true);
    } catch {
      setMessage("‚ùå Something went wrong");
      setHint("Please try again");
      playSad();
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      <h1>Apply Today ‚Äì Join the RGM Family</h1>

      <p>
        Thank you for your interest in <strong>RGM Family</strong>. Please
        complete the application below.
      </p>

      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        {hint && <div className="speech-bubble">{hint}</div>}
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>
        {/* FORM CONTENT UNCHANGED */}
      </form>
    </div>
  );
}

export default ApplyToday;
