import React, { useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import "../../styles/Founder.css";

/* IMAGES for Hero + Founder Story */
import f1 from "../../assets/gallery/images/gallery32.jpg";
import f2 from "../../assets/gallery/images/gallery33.jpg";
import f3 from "../../assets/gallery/images/gallery34.jpg";
import f4 from "../../assets/gallery/images/gallery35.jpg";
import f5 from "../../assets/gallery/images/gallery36.jpg";
import f6 from "../../assets/gallery/images/gallery37.jpg";

const heroImages = [f1, f2, f3, f4];
const storyImages = [f5, f6];

function Founder() {
  const sectionRefs = useRef([]);
  const [heroIndex, setHeroIndex] = useState(0);
  const [storyIndex, setStoryIndex] = useState(0);

  /* HERO SLIDES */
  useEffect(() => {
    const t = setInterval(
      () => setHeroIndex((p) => (p + 1) % heroImages.length),
      5000
    );
    return () => clearInterval(t);
  }, []);

  /* STORY SLIDES */
  useEffect(() => {
    const t = setInterval(
      () => setStoryIndex((p) => (p + 1) % storyImages.length),
      6000
    );
    return () => clearInterval(t);
  }, []);

  /* SCROLL ANIMATIONS */
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) =>
        entries.forEach((e) => e.isIntersecting && e.target.classList.add("animate-in")),
      { threshold: 0.15 }
    );

    sectionRefs.current.forEach((el) => el && observer.observe(el));
    return () => observer.disconnect();
  }, []);

  const setRefs = (el, i) => (sectionRefs.current[i] = el);

  return (
    <div className="founder-container">

      {/* HERO */}
      <section className="founder-hero">
        <div className="hero-image-box">
          <img src={heroImages[heroIndex]} alt="Founder Vision" />
        </div>

        <div className="hero-content">
          <h1>Our Visionary Leader</h1>
          <p>
            The heart behind RGM ‚Äî driving growth, values, and innovation silently but powerfully.
          </p>
        </div>
      </section>

      {/* FOUNDER STORY */}
      <section className="founder-story" ref={(el) => setRefs(el, 0)}>
        <div className="story-text">
          <h2>The Philosophy Behind RGM</h2>
          <p>
            At the core of RGM is a vision that values people, integrity, and relentless growth.
            Our founder believes that a company thrives when its people feel empowered and part of a shared mission.
          </p>
          <p>
            While the face may be unseen, the principles are visible in every driver, every partner, and every decision we make.
          </p>
        </div>

        <div className="story-visual">
          <img src={storyImages[storyIndex]} alt="Founder Inspiration" />
        </div>
      </section>

      {/* FOUNDER VALUES */}
      <section className="founder-values" ref={(el) => setRefs(el, 1)}>
        <h2>Guiding Principles</h2>
        <div className="values-grid">
          <div className="value-card">
            <h3>üí° Vision-Driven</h3>
            <p>Every decision and strategy is rooted in a long-term vision for growth and impact.</p>
          </div>
          <div className="value-card">
            <h3>ü§ù People-First</h3>
            <p>Empowering our people and treating them as family is the foundation of our success.</p>
          </div>
          <div className="value-card">
            <h3>‚öñ Integrity & Ethics</h3>
            <p>Transparency and fairness guide every partnership and internal process.</p>
          </div>
          <div className="value-card">
            <h3>üöÄ Relentless Growth</h3>
            <p>Innovation and ambition fuel progress ‚Äî without losing sight of our values.</p>
          </div>
        </div>
      </section>

      {/* CTA */}
      <section className="founder-cta" ref={(el) => setRefs(el, 2)}>
        <h2>Be Part of Our Vision</h2>
        <p>
          Join a company guided by strong values, visionary leadership, and a people-first culture.
          Apply now to grow with a team that‚Äôs building something extraordinary.
        </p>
        <Link to="/join/apply" className="cta-apply-btn">
          Apply Now
        </Link>
      </section>

    </div>
  );
}

export default Founder;
