import { useState, useRef } from "react";
import Lottie from "lottie-react";
import "../../styles/ApplyToday.css";
import characterAnimation from "../../assets/animations/character.json";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("");
  const [completed, setCompleted] = useState(0);

  const lottieRef = useRef();

  const requiredFields = [
    "firstName","lastName","ssn","dob","license",
    "address1","city","state","zip","residence3yrs",
    "primaryPhone","email","confirmEmail","consent"
  ];

  const updateProgress = (updatedForm) => {
    const filled = requiredFields.filter(f =>
      f === "consent" ? updatedForm[f] : updatedForm[f]?.length
    ).length;
    setCompleted(filled);
  };

  const playHappy = () => lottieRef.current?.playSegments([60, 90], true);
  const playExcited = () => lottieRef.current?.playSegments([91, 120], true);
  const playConcern = () => lottieRef.current?.playSegments([121, 150], true);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const updatedForm = { ...form, [name]: type === "checkbox" ? checked : value };
    setForm(updatedForm);
    updateProgress(updatedForm);

    switch (name) {
      case "firstName":
        value ? playHappy() : playConcern();
        setHint(value ? "Nice! First name looks good." : "Please enter your first name.");
        break;

      case "lastName":
        value ? playHappy() : playConcern();
        setHint(value ? "Last name added." : "Last name is required.");
        break;

      case "email":
        if (!value.includes("@")) {
          playConcern();
          setHint("Email format looks incorrect.");
        } else {
          playExcited();
          setHint("Email looks perfect.");
        }
        break;

      case "confirmEmail":
        if (value !== form.email) {
          playConcern();
          setHint("Emails do not match.");
        } else {
          playExcited();
          setHint("Emails match perfectly!");
        }
        break;

      case "primaryPhone":
        if (value.length < 10) {
          playConcern();
          setHint("Phone number seems too short.");
        } else {
          playHappy();
          setHint("Phone number looks good.");
        }
        break;

      case "consent":
        checked ? playExcited() : playConcern();
        setHint(checked ? "All set! You can submit now." : "Consent is required.");
        break;

      default:
        setHint("");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      setMessage("‚ùå Emails do not match.");
      playConcern();
      return;
    }

    if (!form.consent) {
      setMessage("‚ùå Consent is required.");
      playConcern();
      return;
    }

    playExcited();
    setMessage("üéâ Application submitted successfully!");
  };

  return (
    <div className="apply-container">
      <h1>Apply Today ‚Äì Join the RGM Family</h1>

      <div className="progress-text">
        {completed}/{requiredFields.length} required fields completed
      </div>

      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        {hint && <div className="speech-bubble">{hint}</div>}
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>
        <div className="section-card full">
          <h3>Personal Information</h3>
          <input name="firstName" placeholder="First Name *" value={form.firstName} onChange={handleChange} />
          <input name="middleName" placeholder="Middle Name" value={form.middleName} onChange={handleChange} />
          <input name="lastName" placeholder="Last Name *" value={form.lastName} onChange={handleChange} />
          <input name="ssn" placeholder="SSN *" value={form.ssn} onChange={handleChange} />
          <input type="date" name="dob" value={form.dob} onChange={handleChange} />
          <input name="license" placeholder="License Number *" value={form.license} onChange={handleChange} />
        </div>

        <div className="section-card full">
          <h3>Address</h3>
          <input name="address1" placeholder="Address *" value={form.address1} onChange={handleChange} />
          <input name="city" placeholder="City *" value={form.city} onChange={handleChange} />
          <input name="state" placeholder="State *" value={form.state} onChange={handleChange} />
          <input name="zip" placeholder="ZIP *" value={form.zip} onChange={handleChange} />
        </div>

        <div className="section-card full">
          <h3>Contact</h3>
          <input name="primaryPhone" placeholder="Phone *" value={form.primaryPhone} onChange={handleChange} />
          <input name="email" placeholder="Email *" value={form.email} onChange={handleChange} />
          <input name="confirmEmail" placeholder="Confirm Email *" value={form.confirmEmail} onChange={handleChange} />
          <label className="checkbox-container">
            <input type="checkbox" name="consent" checked={form.consent} onChange={handleChange} />
            I agree to consent
          </label>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn full">Submit Application</button>
      </form>
    </div>
  );
}

export default ApplyToday;
