import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../../styles/Truckload.css";

// Hero images
import hero1 from "../../assets/gallery/images/gallery1.jpg";
import hero2 from "../../assets/gallery/images/gallery2.jpg";
import hero3 from "../../assets/gallery/images/gallery3.jpg";

// Main slideshow images
import main1 from "../../assets/gallery/images/gallery4.jpg";
import main2 from "../../assets/gallery/images/gallery5.jpg";
import main3 from "../../assets/gallery/images/gallery6.jpg";
import main4 from "../../assets/gallery/images/gallery7.jpg";
import main5 from "../../assets/gallery/images/gallery8.jpg";

function Truckload() {
  const heroSlides = [hero1, hero2, hero3];
  const mainSlides = [main1, main2, main3, main4, main5];

  const [heroIndex, setHeroIndex] = useState(0);
  const [mainIndex, setMainIndex] = useState(0);

  // Hero slideshow
  useEffect(() => {
    const heroTimer = setInterval(() => {
      setHeroIndex((prev) => (prev + 1) % heroSlides.length);
    }, 4000);
    return () => clearInterval(heroTimer);
  }, []);

  // Main slideshow
  useEffect(() => {
    const mainTimer = setInterval(() => {
      setMainIndex((prev) => (prev + 1) % mainSlides.length);
    }, 5000);
    return () => clearInterval(mainTimer);
  }, []);

  // Reveal animations on scroll
  useEffect(() => {
    const revealElements = document.querySelectorAll(".reveal");
    const revealOnScroll = () => {
      for (let el of revealElements) {
        const top = el.getBoundingClientRect().top;
        const windowHeight = window.innerHeight;
        if (top < windowHeight - 100) el.classList.add("visible");
      }
    };
    window.addEventListener("scroll", revealOnScroll);
    revealOnScroll();
    return () => window.removeEventListener("scroll", revealOnScroll);
  }, []);

  return (
    <div className="rgm-truckload-page">

      {/* HERO */}
      <section className="rgm-truckload-hero">
        <div className="hero-image-box">
          {heroSlides.map((img, idx) => (
            <img
              key={idx}
              src={img}
              alt={`Hero ${idx + 1}`}
              className={heroIndex === idx ? "active" : ""}
            />
          ))}
        </div>
        <div className="hero-content">
          <h1>Flatbed Truckload Solutions</h1>
          <p>Built on Trust ‚Ä¢ Safety ‚Ä¢ Reliable Flatbed Capacity</p>
        </div>
      </section>

      {/* INTRO */}
      <section className="rgm-truckload-intro reveal">
        <p>
          <strong>RGM Truckload</strong> specializes in full flatbed shipments
          that require experienced drivers, proper securement, and dependable communication.
        </p>
      </section>

      {/* MAIN SLIDESHOW */}
      <section className="rgm-truckload-main reveal">
        <h2>Main Gallery</h2>
        <div className="main-slideshow-frame">
          {mainSlides.map((img, idx) => (
            <img
              key={idx}
              src={img}
              alt={`Main ${idx + 1}`}
              className={mainIndex === idx ? "active" : ""}
            />
          ))}
        </div>
      </section>

      {/* FEATURES */}
      <section className="rgm-truckload-features reveal">
        <h2>Truckload Key Features</h2>
        <div className="features-grid">
          <div className="adv-card">üöõ Flatbed & Stepdeck Capacity</div>
          <div className="adv-card">üîí Securement-Focused Operations</div>
          <div className="adv-card">üß∞ Construction & Industrial Freight</div>
          <div className="adv-card">üìç Regional & Nationwide Coverage</div>
          <div className="adv-card">üì° Real-Time Load Updates</div>
          <div className="adv-card">ü§ù Trusted Owner-Operators</div>
        </div>
      </section>

      {/* CAPABILITIES */}
      <section className="rgm-truckload-capabilities reveal">
        <h2>Truckload Capabilities</h2>
        <div className="features-grid">
          <div className="adv-card">48‚Äô & 53‚Äô Flatbed Loads</div>
          <div className="adv-card">Step Deck & Conestoga</div>
          <div className="adv-card">Oversized & Legal Loads</div>
          <div className="adv-card">Construction Materials</div>
          <div className="adv-card">Machinery & Equipment</div>
          <div className="adv-card">Project-Based Freight</div>
        </div>
      </section>

      {/* CTA */}
      <section className="rgm-truckload-cta reveal">
        <h2 className="cta-headline">Ready to Move a Flatbed Load?</h2>
        <p>
          Contact RGM today for dependable flatbed truckload solutions
          built on trust and accountability.
        </p>
        <div className="cta-buttons">
          <Link to="/contact" className="cta-primary">Get A Quote</Link>
          <Link to="/contact" className="cta-secondary">Click to Call</Link>
        </div>
      </section>

    </div>
  );
}

export default Truckload;
