import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../../styles/Pay.css";

function Pay() {
  const reviews = [
    {
      title: "Great Service",
      text: "Great communication, great service — would gladly work with them again.",
      author: "Verified Broker"
    },
    {
      title: "RGM sets the bar!",
      text: "Absolute pleasure working with RGM. Professional drivers and top-notch service.",
      author: "Verified Broker"
    }
  ];

  const [index, setIndex] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setIndex((prev) => (prev + 1) % reviews.length);
    }, 4000);
    return () => clearInterval(timer);
  }, [reviews.length]);

  return (
    <div className="pay-container">

      {/* HERO */}
      <div className="pay-hero">
        <h1>Driver Pay at RGM Family</h1>
        <p>Maximize your earning potential with performance-based pay</p>
      </div>

      {/* CONTENT */}
      <div className="pay-content">
        <h2>Why Drivers Love RGM Family</h2>
        <p>
          At RGM Family, drivers come first. Our performance-based pay ensures
          your effort is rewarded fairly, transparently, and consistently.
        </p>

        {/* STATS */}
        <div className="stats-grid">
          <div className="stat-card">
            <h3>100%</h3>
            <p>Driver Satisfaction</p>
          </div>
          <div className="stat-card">
            <h3>100%</h3>
            <p>On-Time Delivery</p>
          </div>
          <div className="stat-card">
            <h3>5.0 ★</h3>
            <p>Broker Reviews</p>
          </div>
        </div>
      </div>

      {/* REVIEWS */}
      <section className="reviews-section">
        <h2>What Industry Partners Say</h2>

        <div className="review-carousel">
          <div className="review-card fade">
            <h3>{reviews[index].title}</h3>
            <p>"{reviews[index].text}"</p>
            <span>- {reviews[index].author}</span>
          </div>
        </div>
      </section>

      {/* CTA */}
      <div className="cta-box">
        <h2>Drive It Like You Own It.</h2>
        <Link to="/join/apply" className="apply-btn">
          Apply Now
        </Link>
      </div>

    </div>
  );
}

export default Pay;
