import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../../styles/Pay.css";

// Import hero images
import hero1 from "../../assets/images/hero/hero1.jpg";
import hero2 from "../../assets/images/hero/hero2.jpg";
import hero3 from "../../assets/images/hero/hero3.jpg";
import hero4 from "../../assets/images/hero/hero4.jpg";

// Import screenshot reviews
import review1 from "../../assets/reviews/review1.png";
import review2 from "../../assets/reviews/review2.png";

function Pay() {
  const heroImages = [hero1, hero2, hero3, hero4];

  const textReviews = [
    "RGM Family is amazing! The support from dispatch and trainers is top-notch. — John D.",
    "I love the pay structure here. Performance-based pay really motivates me to do my best. — Sarah M.",
    "The home time is great! I get weekends with my family, which is rare in trucking. — Mike T.",
    "Employee-owned company! Feels good to be a part of a team that actually cares about drivers. — Laura K.",
    "Excellent equipment, always clean and reliable. Makes driving enjoyable. — David P."
  ];

  const imageReviews = [review1, review2];

  const [currentHero, setCurrentHero] = useState(0);
  const [currentText, setCurrentText] = useState(0);
  const [currentImage, setCurrentImage] = useState(0);

  // Hero slideshow
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentHero((prev) => (prev + 1) % heroImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // Text reviews slideshow
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentText((prev) => (prev + 1) % textReviews.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  // Image reviews slideshow
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % imageReviews.length);
    }, 6000);
    return () => clearInterval(timer);
  }, []);

  // Scroll animation for content
  useEffect(() => {
    const animateElements = document.querySelectorAll(
      ".pay-content p, .pay-content h2, .pay-content ul li, .cta-box, .reviews-section"
    );

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("active");
          }
        });
      },
      { threshold: 0.2 }
    );

    animateElements.forEach((el) => observer.observe(el));
    return () => animateElements.forEach((el) => observer.unobserve(el));
  }, []);

  const nextText = () => setCurrentText((prev) => (prev + 1) % textReviews.length);
  const prevText = () => setCurrentText((prev) => (prev - 1 + textReviews.length) % textReviews.length);

  const nextImage = () => setCurrentImage((prev) => (prev + 1) % imageReviews.length);
  const prevImage = () => setCurrentImage((prev) => (prev - 1 + imageReviews.length) % imageReviews.length);

  return (
    <div className="pay-container">

      {/* HERO SECTION */}
      <section
        className="pay-hero"
        style={{ backgroundImage: `url(${heroImages[currentHero]})` }}
      >
        <div className="hero-overlay">
          <h1>Driver Pay at RGM Family</h1>
          <p>Maximize your earning potential with performance-based pay</p>
          <Link to="/join/apply" className="hero-apply-btn">Apply Now</Link>
        </div>
      </section>

      {/* CONTENT */}
      <div className="pay-content">
        <h2>Maximize Your Earning Potential</h2>
        <p>
          At RGM Family, we reward drivers for performance, not just miles.
          Our pay structure ensures your hard work reflects directly in
          your paycheck. Focus on driving — we’ll take care of the rest.
        </p>

        <h2>Additional Pay Opportunities</h2>
        <ul>
          <li>Tarp Pay</li>
          <li>Breakdown Pay</li>
          <li>Referral Bonuses</li>
          <li>Stop Pay</li>
          <li>Detention Pay</li>
          <li>Over-Dimensional Load Pay</li>
          <li>Vacation Pay</li>
          <li>Sign-On Bonuses</li>
        </ul>

        <h2>Our Driver Statistics</h2>
        <div className="stats-section">
          <div className="stat-box">
            <p>Driver Satisfaction</p>
            <div className="progress">
              <div className="progress-fill" style={{ width: "100%" }}></div>
            </div>
            <span>100%</span>
          </div>

          <div className="stat-box">
            <p>Average Pay Increase</p>
            <div className="progress">
              <div className="progress-fill" style={{ width: "95%" }}></div>
            </div>
            <span>95%</span>
          </div>

          <div className="stat-box">
            <p>Retention Rate</p>
            <div className="progress">
              <div className="progress-fill" style={{ width: "90%" }}></div>
            </div>
            <span>90%</span>
          </div>
        </div>
      </div>

      {/* CTA BOX */}
      <div className="cta-box">
        <h2>Drive It Like You Own It.</h2>
        <Link to="/join/apply" className="apply-btn">Apply Now</Link>
      </div>

      {/* REVIEWS SECTION */}
      <div className="reviews-section">
        <h2>RGM Family Driver Reviews</h2>

        {/* Text Reviews Slider */}
        <div className="reviews-slider">
          <button className="review-btn left" onClick={prevText}>&#10094;</button>
          <div className="review-text fade">{textReviews[currentText]}</div>
          <button className="review-btn right" onClick={nextText}>&#10095;</button>
        </div>

        {/* Image Reviews Slider */}
        <div className="reviews-slider image-slider">
          <button className="review-btn left" onClick={prevImage}>&#10094;</button>
          <img src={imageReviews[currentImage]} alt="RGM Review" className="review-img fade" />
          <button className="review-btn right" onClick={nextImage}>&#10095;</button>
        </div>

        {/* Reference Link */}
        <p className="review-ref">
          Reviews source:{" "}
          <a
            href="https://www.carriersource.io/carriers/rgm-line-haul-inc#reviews"
            target="_blank"
            rel="noreferrer"
          >
            CarrierSource
          </a>
        </p>
      </div>
    </div>
  );
}

export default Pay;
