import { useState } from "react";
import Lottie from "lottie-react";
import "../../styles/ApplyToday.css";
import characterAnim from "../../assets/animations/character.json";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [speech, setSpeech] = useState("üëã Hi! Fill the form carefully");

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === "checkbox" ? checked : value });
    setSpeech(`‚úçÔ∏è Entering ${name}`);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      setSpeech("‚ùå Emails do not match");
      alert("Email addresses do not match");
      return;
    }

    if (!form.consent) {
      setSpeech("‚ö†Ô∏è Consent required");
      alert("Please provide consent to proceed");
      return;
    }

    try {
      setLoading(true);
      setMessage("");
      setSpeech("‚è≥ Submitting...");

      const res = await fetch("http://localhost:5000/apply", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      if (!res.ok) throw new Error();

      setSpeech("üéâ Application submitted!");
      setMessage(
        "‚úÖ Application submitted successfully! Our team will contact you shortly for your virtual meeting."
      );

    } catch {
      setSpeech("‚ùå Submission failed");
      setMessage("‚ùå Something went wrong. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      {/* CHARACTER OVERLAY (SAFE) */}
      <div className="assistant-character">
        <Lottie animationData={characterAnim} loop autoplay />
        <div className="assistant-speech">{speech}</div>
      </div>

      {/* üîí YOUR ORIGINAL CONTENT ‚Äî UNTOUCHED */}
      <h1>Apply Today ‚Äì Join the RGM Family</h1>
      <p>
        Thank you for your interest in <strong>RGM Family</strong>. Please
        complete the application below. Incomplete applications may delay
        processing.
      </p>

      <form className="apply-form" onSubmit={handleSubmit}>
        {/* üîí ALL YOUR FORM SECTIONS EXACTLY SAME */}
        {/* (NO changes made here intentionally) */}

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn full" type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit Application & Request Meeting"}
        </button>
      </form>
    </div>
  );
}

export default ApplyToday;
