import { useState } from "react";
import "../../styles/ApplyToday.css";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      alert("Email addresses do not match");
      return;
    }

    if (!form.consent) {
      alert("Please provide consent to proceed");
      return;
    }

    try {
      setLoading(true);
      setMessage("");

      const res = await fetch("http://localhost:5000/apply", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(form)
      });

      if (!res.ok) {
        throw new Error("Failed to submit application");
      }

      setMessage(
        "✅ Application submitted successfully! Our team will contact you shortly for your virtual meeting."
      );

      setForm({
        firstName: "",
        middleName: "",
        lastName: "",
        suffix: "",
        ssn: "",
        dob: "",
        address1: "",
        address2: "",
        country: "United States",
        city: "",
        state: "",
        zip: "",
        residence3yrs: "",
        primaryPhone: "",
        cellPhone: "",
        email: "",
        confirmEmail: "",
        consent: false,
        license: ""
      });
    } catch (err) {
      setMessage("❌ Something went wrong. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      <h1>Apply Today – Join the RGM Family</h1>

      <p>
        Thank you for your interest in <strong>RGM Family</strong>. Please
        complete the application below. Incomplete applications may delay
        processing.
      </p>

      <form className="apply-form" onSubmit={handleSubmit}>
        {/* PERSONAL INFO */}
        <div className="section-card full">
          <h3>Personal Information</h3>

          <input
            name="firstName"
            placeholder="First Name *"
            required
            value={form.firstName}
            onChange={handleChange}
          />

          <input
            name="middleName"
            placeholder="Middle Name"
            value={form.middleName}
            onChange={handleChange}
          />

          <input
            name="lastName"
            placeholder="Last Name *"
            required
            value={form.lastName}
            onChange={handleChange}
          />

          <input
            name="suffix"
            placeholder="Suffix"
            value={form.suffix}
            onChange={handleChange}
          />

          <input
            name="ssn"
            placeholder="SSN / SIN *"
            required
            value={form.ssn}
            onChange={handleChange}
          />

          <input
            type="date"
            name="dob"
            required
            value={form.dob}
            onChange={handleChange}
          />

          <input
            name="license"
            placeholder="Driver License Number *"
            required
            value={form.license}
            onChange={handleChange}
          />
        </div>

        {/* ADDRESS */}
        <div className="section-card full">
          <h3>Address</h3>

          <input
            name="address1"
            placeholder="Street Address Line 1 *"
            required
            value={form.address1}
            onChange={handleChange}
          />

          <input
            name="address2"
            placeholder="Street Address Line 2"
            value={form.address2}
            onChange={handleChange}
          />

          <input
            name="city"
            placeholder="City *"
            required
            value={form.city}
            onChange={handleChange}
          />

          <input
            name="state"
            placeholder="State / Province *"
            required
            value={form.state}
            onChange={handleChange}
          />

          <input
            name="zip"
            placeholder="Zip / Postal Code *"
            required
            value={form.zip}
            onChange={handleChange}
          />

          <select
            name="residence3yrs"
            required
            value={form.residence3yrs}
            onChange={handleChange}
          >
            <option value="">Residence 3+ years?</option>
            <option value="Yes">Yes</option>
            <option value="No">No</option>
          </select>
        </div>

        {/* CONTACT */}
        <div className="section-card full">
          <h3>Contact</h3>

          <input
            name="primaryPhone"
            placeholder="Primary Phone *"
            required
            value={form.primaryPhone}
            onChange={handleChange}
          />

          <input
            name="cellPhone"
            placeholder="Cell Phone"
            value={form.cellPhone}
            onChange={handleChange}
          />

          <input
            name="email"
            type="email"
            placeholder="Email *"
            required
            value={form.email}
            onChange={handleChange}
          />

          <input
            name="confirmEmail"
            type="email"
            placeholder="Confirm Email *"
            required
            value={form.confirmEmail}
            onChange={handleChange}
          />

          {/* ✅ FIXED CHECKBOX */}
          <div className="consent-wrapper">
            <label className="consent-label">
              <input
                type="checkbox"
                name="consent"
                checked={form.consent}
                onChange={handleChange}
              />
              <span>
                I consent to receive communication and for confirmation
              </span>
            </label>
          </div>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn full" type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit Application & Request Meeting"}
        </button>
      </form>
    </div>
  );
}

export default ApplyToday;
