import React, { useEffect, useRef, useState } from "react";
import "../../styles/Responsibility.css";

/* ===== IMAGE IMPORTS (gallery19 ‚Üí gallery31) ===== */
import g19 from "../../assets/gallery/images/gallery19.jpg";
import g20 from "../../assets/gallery/images/gallery20.jpg";
import g21 from "../../assets/gallery/images/gallery21.jpg";
import g22 from "../../assets/gallery/images/gallery22.jpg";
import g23 from "../../assets/gallery/images/gallery23.jpg";
import g24 from "../../assets/gallery/images/gallery24.jpg";
import g25 from "../../assets/gallery/images/gallery25.jpg";
import g26 from "../../assets/gallery/images/gallery26.jpg";
import g27 from "../../assets/gallery/images/gallery27.jpg";
import g28 from "../../assets/gallery/images/gallery28.jpg";
import g29 from "../../assets/gallery/images/gallery29.jpg";
import g30 from "../../assets/gallery/images/gallery30.jpg";
import g31 from "../../assets/gallery/images/gallery31.jpg";

/* Slides */
const heroImages = [g19, g20, g21, g22];
const mainImages = [g23, g24, g25, g26, g27, g28, g29, g30, g31];

function Responsibility() {
  const sectionRefs = useRef([]);
  const [heroIndex, setHeroIndex] = useState(0);
  const [mainIndex, setMainIndex] = useState(0);

  /* HERO SLIDESHOW */
  useEffect(() => {
    const timer = setInterval(() => {
      setHeroIndex((prev) => (prev + 1) % heroImages.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  /* MAIN SLIDESHOW */
  useEffect(() => {
    const timer = setInterval(() => {
      setMainIndex((prev) => (prev + 1) % mainImages.length);
    }, 4000);
    return () => clearInterval(timer);
  }, []);

  /* SCROLL ANIMATION */
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("animate-in");
          }
        });
      },
      { threshold: 0.15 }
    );

    sectionRefs.current.forEach((el) => el && observer.observe(el));
    return () => observer.disconnect();
  }, []);

  const setRefs = (el, i) => (sectionRefs.current[i] = el);

  return (
    <div className="rgm-responsibility-container">

      {/* ===== HERO ===== */}
      <section className="rgm-hero">
        <div className="hero-image-box">
          <img src={heroImages[heroIndex]} alt="Corporate Responsibility" />
        </div>

        <div className="hero-content">
          <h1>Corporate Responsibility at RGM Family</h1>
          <p>Driven by Purpose ‚Ä¢ Built on Care ‚Ä¢ Committed to Doing Right</p>
        </div>
      </section>

      {/* ===== MAIN SLIDESHOW ===== */}
      <section className="rgm-main-slideshow" ref={(el) => setRefs(el, 0)}>
        <h2>Responsibility in Action</h2>

        <div className="slideshow-frame">
          <img src={mainImages[mainIndex]} alt="RGM Responsibility" />
        </div>

        <div className="slideshow-tags">
          <span>Community</span>
          <span>People First</span>
          <span>Sustainability</span>
          <span>Integrity</span>
        </div>
      </section>

      {/* ===== COMMUNITY ===== */}
      <section className="rgm-section fade-slide" ref={(el) => setRefs(el, 1)}>
        <div className="icon-bg community">ü§ù</div>
        <h2>Supporting Our Communities</h2>
        <p>
          Even in our first year, RGM Family is committed to helping local
          organizations that strengthen families, children, veterans, and
          neighborhoods.
        </p>
        <ul>
          <li>Local food banks & hunger relief</li>
          <li>Children‚Äôs education programs</li>
          <li>Veteran assistance initiatives</li>
          <li>Animal rescue & environmental causes</li>
        </ul>
      </section>

      {/* ===== FAMILY ===== */}
      <section className="rgm-section fade-slide" ref={(el) => setRefs(el, 2)}>
        <div className="icon-bg family">‚ù§Ô∏è</div>
        <h2>Family Assistance Fund</h2>
        <p>
          Our employees are our family. When life happens, RGM is there ‚Äî
          supporting team members through hardships and celebrating milestones
          together.
        </p>
      </section>

      {/* ===== ENVIRONMENT ===== */}
      <section className="rgm-section fade-slide" ref={(el) => setRefs(el, 3)}>
        <div className="icon-bg environment">üå±</div>
        <h2>Environmental Responsibility</h2>
        <p>
          We are committed to reducing our footprint while operating responsibly
          across every mile.
        </p>
        <ul>
          <li>Fuel-efficiency driver education</li>
          <li>Idle-time reduction incentives</li>
          <li>Digital paperwork initiatives</li>
          <li>Optimized routing & maintenance</li>
        </ul>
      </section>

      {/* ===== ETHICS ===== */}
      <section className="rgm-section fade-slide" ref={(el) => setRefs(el, 4)}>
        <div className="icon-bg ethics">‚öñÔ∏è</div>
        <h2>Integrity & Ethics</h2>
        <p>
          Transparency, fairness, and respect guide every decision at RGM.
          Ethical operations and people-first values are non-negotiable.
        </p>
      </section>

    </div>
  );
}

export default Responsibility;
