import React, { useState } from "react";
import "./Gallery.css";
import Footer from "../components/Footer";

// ðŸ”¥ LOAD ALL IMAGES
const imageContext = require.context(
  "../assets/gallery/images",
  false,
  /\.(png|jpe?g|webp)$/
);

// ðŸ”¥ LOAD ALL VIDEOS
const videoContext = require.context(
  "../assets/gallery/videos",
  false,
  /\.(mp4|webm)$/
);

const images = imageContext.keys().map((key) => ({
  type: "image",
  src: imageContext(key),
}));

const videos = videoContext.keys().map((key) => ({
  type: "video",
  src: videoContext(key),
}));

const media = [...images, ...videos];

const Gallery = () => {
  const [active, setActive] = useState(null);

  return (
    <>
      <div className="gallery-page">
        <h1 className="gallery-title">RGM Gallery</h1>
        <p className="gallery-subtitle">
          Moments from our fleet, family, and journeys on the road
        </p>

        <div className="gallery-grid">
          {media.map((item, index) => (
            <div
              key={index}
              className="gallery-item"
              onClick={() => setActive(item)}
            >
              {item.type === "image" ? (
                <img src={item.src} alt="RGM Gallery" />
              ) : (
                <video src={item.src} muted />
              )}
              <div className="gallery-overlay">View</div>
            </div>
          ))}
        </div>

        {/* LIGHTBOX */}
        {active && (
          <div className="lightbox" onClick={() => setActive(null)}>
            <div className="lightbox-content">
              {active.type === "image" ? (
                <img src={active.src} alt="Preview" />
              ) : (
                <video src={active.src} controls autoPlay />
              )}
            </div>
          </div>
        )}
      </div>

      <Footer />
    </>
  );
};

export default Gallery;
