import { useState, useRef, useEffect } from "react";
import Lottie from "lottie-react";
import "../../styles/ApplyToday.css";
import characterAnimation from "../../assets/animations/character.json";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState(""); // Speech bubble hint
  const [eyeEmotion, setEyeEmotion] = useState("neutral"); // neutral, happy, sad
  const [eyeExcited, setEyeExcited] = useState(false);

  const lottieRef = useRef();

  // Handle eye position based on cursor
  const [eyePosition, setEyePosition] = useState({ x: 0, y: 0 });
  useEffect(() => {
    const handleMouseMove = (e) => {
      const rect = document.querySelector(".character-wrapper")?.getBoundingClientRect();
      if (!rect) return;
      const centerX = rect.left + rect.width / 2;
      const centerY = rect.top + rect.height / 2;
      setEyePosition({
        x: (e.clientX - centerX) / 15,
        y: (e.clientY - centerY) / 15
      });
    };
    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  // Random blinking
  const [isBlinking, setIsBlinking] = useState(false);
  useEffect(() => {
    const blinkInterval = setInterval(() => {
      setIsBlinking(true);
      setTimeout(() => setIsBlinking(false), 200);
    }, Math.random() * 4000 + 3000);
    return () => clearInterval(blinkInterval);
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === "checkbox" ? checked : value });

    // Animate character based on current field
    switch (name) {
      case "firstName":
        lottieRef.current?.playSegments([0, 30], true);
        setHint("Great! Enter your first name.");
        if (value) triggerExcitedEyes();
        break;
      case "lastName":
        lottieRef.current?.playSegments([31, 60], true);
        setHint("Nice! Now your last name.");
        if (value) triggerExcitedEyes();
        break;
      case "email":
        lottieRef.current?.playSegments([61, 90], true);
        setHint("Double-check your email.");
        if (value.includes("@")) triggerExcitedEyes();
        break;
      case "primaryPhone":
        lottieRef.current?.playSegments([91, 120], true);
        setHint("Phone number, please!");
        if (value.length >= 10) triggerExcitedEyes();
        break;
      default:
        setHint("");
        setEyeEmotion("neutral");
    }
  };

  const triggerExcitedEyes = () => {
    setEyeEmotion("happy");
    setEyeExcited(true);
    setTimeout(() => setEyeExcited(false), 500);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      setMessage("‚ùå Emails do not match!");
      lottieRef.current?.playSegments([121, 150], true);
      setEyeEmotion("sad");
      return;
    }

    if (!form.consent) {
      setMessage("‚ùå Please give consent!");
      lottieRef.current?.playSegments([121, 150], true);
      setEyeEmotion("sad");
      return;
    }

    try {
      setLoading(true);
      setMessage("");
      lottieRef.current?.playSegments([151, 180], true);

      const res = await fetch("http://localhost:5000/apply", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      if (!res.ok) throw new Error("Failed");

      setMessage("üéâ Application submitted successfully!");
      lottieRef.current?.playSegments([181, 210], true);
      setEyeEmotion("happy");

      setForm({
        firstName: "",
        middleName: "",
        lastName: "",
        suffix: "",
        ssn: "",
        dob: "",
        address1: "",
        address2: "",
        country: "United States",
        city: "",
        state: "",
        zip: "",
        residence3yrs: "",
        primaryPhone: "",
        cellPhone: "",
        email: "",
        confirmEmail: "",
        consent: false,
        license: ""
      });
    } catch (err) {
      setMessage("‚ùå Something went wrong. Try again.");
      lottieRef.current?.playSegments([121, 150], true);
      setEyeEmotion("sad");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      <h1>Apply Today ‚Äì Join the RGM Family</h1>

      <p>
        Thank you for your interest in <strong>RGM Family</strong>. Please
        complete the application below. Incomplete applications may delay
        processing.
      </p>

      {/* CHARACTER AT BOTTOM-RIGHT */}
      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        <div
          className={`character-eyes ${eyeEmotion} ${isBlinking ? "blink" : ""} ${
            eyeExcited ? "excited" : ""
          }`}
          style={{ transform: `translate(${eyePosition.x}px, ${eyePosition.y}px)` }}
        />
        {hint && <div className="speech-bubble">{hint}</div>}
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>
        <div className="section-card full">
          <h3>Personal Information</h3>
          <input name="firstName" placeholder="First Name *" required value={form.firstName} onChange={handleChange} />
          <input name="middleName" placeholder="Middle Name" value={form.middleName} onChange={handleChange} />
          <input name="lastName" placeholder="Last Name *" required value={form.lastName} onChange={handleChange} />
          <input name="suffix" placeholder="Suffix" value={form.suffix} onChange={handleChange} />
          <input name="ssn" placeholder="SSN / SIN *" required value={form.ssn} onChange={handleChange} />
          <input type="date" name="dob" required value={form.dob} onChange={handleChange} />
          <input name="license" placeholder="Driver License Number *" required value={form.license} onChange={handleChange} />
        </div>

        <div className="section-card full">
          <h3>Address</h3>
          <input name="address1" placeholder="Street Address Line 1 *" required value={form.address1} onChange={handleChange} />
          <input name="address2" placeholder="Street Address Line 2" value={form.address2} onChange={handleChange} />
          <input name="city" placeholder="City *" required value={form.city} onChange={handleChange} />
          <input name="state" placeholder="State / Province *" required value={form.state} onChange={handleChange} />
          <input name="zip" placeholder="Zip / Postal Code *" required value={form.zip} onChange={handleChange} />
          <select name="residence3yrs" required value={form.residence3yrs} onChange={handleChange}>
            <option value="">Residence 3+ years?</option>
            <option value="Yes">Yes</option>
            <option value="No">No</option>
          </select>
        </div>

        <div className="section-card full">
          <h3>Contact</h3>
          <input name="primaryPhone" placeholder="Primary Phone *" required value={form.primaryPhone} onChange={handleChange} />
          <input name="cellPhone" placeholder="Cell Phone" value={form.cellPhone} onChange={handleChange} />
          <input name="email" type="email" placeholder="Email *" required value={form.email} onChange={handleChange} />
          <input name="confirmEmail" type="email" placeholder="Confirm Email *" required value={form.confirmEmail} onChange={handleChange} />

          <div className="checkbox-container">
            <input type="checkbox" name="consent" checked={form.consent} onChange={handleChange} />
            <span>I consent to receive communication and for the confirmation</span>
          </div>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn full" type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit Application & Request Meeting"}
        </button>
      </form>
    </div>
  );
}

export default ApplyToday;
