import React, { useEffect, useState } from "react";
import "../../styles/WhyRGM.css";

/* HERO SLIDES */
import g10 from "../../assets/gallery/images/gallery10.jpg";
import g11 from "../../assets/gallery/images/gallery11.jpg";
import g12 from "../../assets/gallery/images/gallery12.jpg";
import g13 from "../../assets/gallery/images/gallery13.jpg";
import g14 from "../../assets/gallery/images/gallery14.jpg";
import g15 from "../../assets/gallery/images/gallery15.jpg";
import g16 from "../../assets/gallery/images/gallery16.jpg";
import g17 from "../../assets/gallery/images/gallery17.jpg";
import g18 from "../../assets/gallery/images/gallery18.jpg";
import g19 from "../../assets/gallery/images/gallery19.jpg";
import g20 from "../../assets/gallery/images/gallery20.jpg";
import g21 from "../../assets/gallery/images/gallery21.jpg";
import g22 from "../../assets/gallery/images/gallery22.jpg";
import g23 from "../../assets/gallery/images/gallery23.jpg";
import g24 from "../../assets/gallery/images/gallery24.jpg";
import g25 from "../../assets/gallery/images/gallery25.jpg";

const images = [
  g10,g11,g12,g13,g14,g15,g16,g17,
  g18,g19,g20,g21,g22,g23,g24,g25
];

function WhyRGM() {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrent((prev) => (prev + 1) % images.length);
    }, 4500);
    return () => clearInterval(timer);
  }, []);

  return (
    <div className="why-page">

      {/* HERO SLIDESHOW */}
      <section
        className="why-hero"
        style={{ backgroundImage: `url(${images[current]})` }}
      >
        <div className="hero-overlay">
          <h1>Why RGM Family</h1>
          <p>
            You‚Äôre not just joining a company ‚Äî you‚Äôre joining a family
            built on trust, respect, and growth.
          </p>
        </div>
      </section>

      {/* INTRO */}
      <section className="why-intro">
        <p>
          RGM is built on people-first values. We believe strong relationships,
          honest communication, and mutual respect create long-term success
          for drivers, customers, and partners alike.
        </p>
      </section>

      {/* VALUES */}
      <section className="why-values">
        <div className="value-card">ü§ù Family-first culture</div>
        <div className="value-card">üõ° Integrity & transparency</div>
        <div className="value-card">üöõ Driver-focused operations</div>
        <div className="value-card">üìà Grow together mindset</div>
        <div className="value-card">üß† Smart & simple technology</div>
        <div className="value-card">üíô Support beyond the job</div>
      </section>

      {/* MAIN IMAGE SLIDESHOW */}
      <section className="fleet-showcase">
        <h2>Our Fleet In Action</h2>
        <div className="fleet-image">
          <img src={images[current]} alt="RGM Fleet" />
        </div>
      </section>

      {/* CTA */}
      <section className="why-cta">
        <h2>Let‚Äôs Talk About Your Future With RGM</h2>
        <button>Contact RGM Family</button>
      </section>

    </div>
  );
}

export default WhyRGM;
