import React, { useEffect, useRef } from "react";
import "../../styles/RGMResponsibility.css";

function RGMResponsibility() {
  const sectionRefs = useRef([]);

  useEffect(() => {
    const sections = [...sectionRefs.current];
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add("animate-in");
          }
        });
      },
      { threshold: 0.1 }
    );

    sections.forEach(ref => ref && observer.observe(ref));

    return () => sections.forEach(ref => ref && observer.unobserve(ref));
  }, []);

  const setRefs = (el, index) => {
    sectionRefs.current[index] = el;
  };

  return (
    <div className="rgm-responsibility-container">

      {/* HERO */}
      <section className="rgm-hero fade-slide" ref={(el) => setRefs(el, 0)}>
        <h1>Corporate Responsibility at RGM Family</h1>
        <p>We believe in giving back — to our people, our communities, and our planet.</p>
      </section>

      {/* COMMUNITY SUPPORT */}
      <section className="rgm-section fade-slide" ref={(el) => setRefs(el, 1)}>
        <h2>Supporting Our Communities</h2>
        <p>
          RGM Family is committed to helping local organizations and causes that 
          improve the lives of families, children, and veterans. Even in our first year, 
          we are proud to support initiatives such as:
        </p>
        <ul>
          <li>Local food banks and hunger relief programs</li>
          <li>Children’s education and welfare programs</li>
          <li>Veteran assistance initiatives</li>
          <li>Animal rescue and environmental efforts</li>
        </ul>
      </section>

      {/* FAMILY ASSISTANCE */}
      <section className="rgm-section fade-slide" ref={(el) => setRefs(el, 2)}>
        <h2>Family Assistance Fund</h2>
        <p>
          At RGM Family, we treat our employees like family. Our Family Assistance Fund 
          (FAF) provides support to team members facing unexpected hardships. 
          Together, we celebrate successes and help each other through challenges.
        </p>
      </section>

      {/* ENVIRONMENTAL RESPONSIBILITY */}
      <section className="rgm-section fade-slide" ref={(el) => setRefs(el, 3)}>
        <h2>Environmental Responsibility</h2>
        <p>
          We aim to reduce our carbon footprint and operate responsibly. Actions include:
        </p>
        <ul>
          <li>Educating drivers on fuel efficiency and safe driving practices</li>
          <li>Maintaining trucks with fuel-optimized settings</li>
          <li>Incentivizing reduced idle time and efficient routing</li>
          <li>Minimizing paper usage with digital processes</li>
        </ul>
      </section>

      {/* ETHICS */}
      <section className="rgm-section fade-slide" ref={(el) => setRefs(el, 4)}>
        <h2>Integrity & Ethics</h2>
        <p>
          RGM Family operates with transparency, honesty, and respect. Our team adheres to 
          strict ethical standards, promoting human rights, fair treatment, and 
          safe workplaces for all employees, partners, and customers.
        </p>
      </section>

    </div>
  );
}

export default RGMResponsibility;
