import React, { useState } from "react";
import "../styles/Gallery.css";

// IMAGES (50)
import img1 from "../assets/gallery/images/img1.jpg";
import img2 from "../assets/gallery/images/img2.jpg";
// ...
// continue till img50
// import img50 from "../assets/gallery/images/img50.jpg";

// VIDEOS (4)
import video1 from "../assets/gallery/videos/video1.mp4";
import video2 from "../assets/gallery/videos/video2.mp4";
import video3 from "../assets/gallery/videos/video3.mp4";
import video4 from "../assets/gallery/videos/video4.mp4";

const images = [
  img1,
  img2,
  // ...
  // img50
];

const videos = [video1, video2, video3, video4];

const Gallery = () => {
  const [activeMedia, setActiveMedia] = useState(null);

  return (
    <div className="gallery-page">
      <h1 className="gallery-title">Life at RGM</h1>
      <p className="gallery-subtitle">
        Our fleet, people, journeys & moments on the road
      </p>

      <div className="gallery-grid">
        {images.map((img, i) => (
          <div
            key={i}
            className="gallery-card"
            onClick={() => setActiveMedia({ type: "image", src: img })}
          >
            <img src={img} alt={`RGM-${i}`} loading="lazy" />
          </div>
        ))}

        {videos.map((vid, i) => (
          <div
            key={i}
            className="gallery-card video-card"
            onClick={() => setActiveMedia({ type: "video", src: vid })}
          >
            <video src={vid} muted />
            <span className="play-icon">▶</span>
          </div>
        ))}
      </div>

      {/* LIGHTBOX */}
      {activeMedia && (
        <div className="lightbox" onClick={() => setActiveMedia(null)}>
          <div className="lightbox-content" onClick={(e) => e.stopPropagation()}>
            {activeMedia.type === "image" ? (
              <img src={activeMedia.src} alt="preview" />
            ) : (
              <video src={activeMedia.src} controls autoPlay />
            )}
            <button className="close-btn">×</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Gallery;
