import React, { useEffect, useState, useRef } from "react";
import "./Gallery.css";
import Footer from "../components/Footer";

/* LOAD IMAGES */
const imageContext = require.context(
  "../assets/gallery/images",
  false,
  /\.(jpg|jpeg|png|webp)$/
);

/* LOAD VIDEOS */
const videoContext = require.context(
  "../assets/gallery/videos",
  false,
  /\.(mp4|webm)$/
);

const images = imageContext.keys().map((k) => ({
  type: "image",
  src: imageContext(k),
}));

const videos = videoContext.keys().map((k) => ({
  type: "video",
  src: videoContext(k),
}));

const allMedia = [...images, ...videos];

const Gallery = () => {
  const [filter, setFilter] = useState("all");
  const [active, setActive] = useState(null);
  const [slide, setSlide] = useState(0);
  const timerRef = useRef(null);

  const featured = images.slice(0, 6); // top slideshow images

  useEffect(() => {
    timerRef.current = setInterval(() => {
      setSlide((prev) => (prev + 1) % featured.length);
    }, 5000);
    return () => clearInterval(timerRef.current);
  }, [featured.length]);

  const filtered =
    filter === "all"
      ? allMedia
      : allMedia.filter((m) => m.type === filter);

  return (
    <>
      {/* HERO SLIDESHOW */}
      <section className="gallery-hero">
        {featured.map((img, i) => (
          <div
            key={i}
            className={`hero-slide ${i === slide ? "active" : ""}`}
            style={{ backgroundImage: `url(${img.src})` }}
          />
        ))}

        <div className="hero-overlay">
          <h1>RGM In Motion</h1>
          <p>Every mile tells a story</p>

          <div className="hero-dots">
            {featured.map((_, i) => (
              <span
                key={i}
                className={i === slide ? "active" : ""}
                onClick={() => setSlide(i)}
              />
            ))}
          </div>
        </div>
      </section>

      {/* FILTER BAR */}
      <div className="gallery-filter-bar">
        {["all", "image", "video"].map((f) => (
          <button
            key={f}
            className={filter === f ? "active" : ""}
            onClick={() => setFilter(f)}
          >
            {f === "all" ? "All Media" : f === "image" ? "Photos" : "Videos"}
          </button>
        ))}
      </div>

      {/* GRID */}
      <section className="gallery-grid">
        {filtered.map((item, i) => (
          <div
            key={i}
            className="gallery-card"
            onClick={() => setActive(item)}
          >
            {item.type === "image" ? (
              <img src={item.src} alt="RGM" />
            ) : (
              <>
                <video src={item.src} muted />
                <div className="play-ripple">â–¶</div>
              </>
            )}
          </div>
        ))}
      </section>

      {/* LIGHTBOX */}
      {active && (
        <div className="lightbox" onClick={() => setActive(null)}>
          <div className="lightbox-inner">
            {active.type === "image" ? (
              <img src={active.src} alt="Preview" />
            ) : (
              <video src={active.src} controls autoPlay />
            )}
          </div>
        </div>
      )}

      <Footer />
    </>
  );
};

export default Gallery;
