import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../../styles/Pay.css";

/* HERO IMAGES */
import hero1 from "../../assets/images/hero/hero1.jpg";
import hero2 from "../../assets/images/hero/hero2.jpg";
import hero3 from "../../assets/images/hero/hero3.jpg";
import hero4 from "../../assets/images/hero/hero4.jpg";

/* REVIEW IMAGES */
import review1 from "../../assets/reviews/review1.png";
import review2 from "../../assets/reviews/review2.png";

function Pay() {
  const heroImages = [hero1, hero2, hero3, hero4];

  const textReviews = [
    "RGM Family is amazing! The support from dispatch and trainers is top-notch. — John D.",
    "I love the pay structure here. Performance-based pay really motivates me to do my best. — Sarah M.",
    "The home time is great! I get weekends with my family, which is rare in trucking. — Mike T.",
    "Employee-owned company! Feels good to be part of a team that truly cares. — Laura K.",
    "Excellent equipment, always clean and reliable. Makes driving enjoyable. — David P.",
    "Pay is always on time, complete transparency, and no force dispatch with high accountability. — Navdeep Kataria"
  ];

  const imageReviews = [review1, review2];

  const [currentHero, setCurrentHero] = useState(0);
  const [currentText, setCurrentText] = useState(0);
  const [currentImage, setCurrentImage] = useState(0);

  /* HERO SLIDER */
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentHero((prev) => (prev + 1) % heroImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  /* TEXT REVIEWS */
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentText((prev) => (prev + 1) % textReviews.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  /* IMAGE REVIEWS */
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % imageReviews.length);
    }, 6000);
    return () => clearInterval(timer);
  }, []);

  /* SCROLL ANIMATION + BAR FILL */
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("active");
          }
        });
      },
      { threshold: 0.3 }
    );

    document.querySelectorAll(".animate").forEach((el) => observer.observe(el));
    return () =>
      document.querySelectorAll(".animate").forEach((el) => observer.unobserve(el));
  }, []);

  return (
    <div className="pay-container">

      {/* HERO */}
      <section
        className="pay-hero"
        style={{ backgroundImage: `url(${heroImages[currentHero]})` }}
      >
        <div className="hero-overlay">
          <h1>Driver Pay at RGM Family</h1>
          <p>Performance-based pay with real transparency</p>
          <Link to="/join/apply" className="hero-apply-btn">Apply Now</Link>
        </div>
      </section>

      {/* CONTENT */}
      <div className="pay-content">
        <h2 className="animate">Maximize Your Earning Potential</h2>
        <p className="animate">
          At RGM Family, we reward performance — not pressure.
          Your effort, safety, and accountability directly reflect in your paycheck.
        </p>

        <h2 className="animate">Additional Pay Opportunities</h2>
        <ul>
          <li className="animate">Tarp Pay</li>
          <li className="animate">Extra Stop Pay</li>
          <li className="animate">Detention Pay</li>
          <li className="animate">Over-Dimensional Load Pay</li>
        </ul>

        <h2 className="animate">Our Driver Statistics</h2>

        <div className="stats-section animate">
          <div className="stat-box">
            <p>Driver Satisfaction</p>
            <div className="progress">
              <div className="progress-fill" data-fill="100%" />
            </div>
            <span>100%</span>
          </div>

          <div className="stat-box">
            <p>Average Pay Increase</p>
            <div className="progress">
              <div className="progress-fill" data-fill="95%" />
            </div>
            <span>95%</span>
          </div>

          <div className="stat-box">
            <p>Retention Rate</p>
            <div className="progress">
              <div className="progress-fill" data-fill="97.7%" />
            </div>
            <span>97.7%</span>
          </div>
        </div>
      </div>

      {/* CTA */}
      <div className="cta-box animate">
        <h2>Drive It Like You Own It.</h2>
        <Link to="/join/apply" className="apply-btn">Apply Now</Link>
      </div>

      {/* REVIEWS */}
      <div className="reviews-section animate">
        <h2>RGM Family Driver Reviews</h2>

        <div className="reviews-slider">
          <button className="review-btn left" onClick={() => setCurrentText((prev) => (prev - 1 + textReviews.length) % textReviews.length)}>
            &#10094;
          </button>

          <div className="review-text fade">{textReviews[currentText]}</div>

          <button className="review-btn right" onClick={() => setCurrentText((prev) => (prev + 1) % textReviews.length)}>
            &#10095;
          </button>
        </div>

        <div className="reviews-slider image-slider">
          <button className="review-btn left" onClick={() => setCurrentImage((prev) => (prev - 1 + imageReviews.length) % imageReviews.length)}>
            &#10094;
          </button>

          <img src={imageReviews[currentImage]} alt="RGM Review" className="review-img fade" />

          <button className="review-btn right" onClick={() => setCurrentImage((prev) => (prev + 1) % imageReviews.length)}>
            &#10095;
          </button>
        </div>

        <p className="review-ref">
          Reviews source:{" "}
          <a href="https://www.carriersource.io/carriers/rgm-line-haul-inc#reviews" target="_blank" rel="noreferrer">
            CarrierSource
          </a>
        </p>
      </div>
    </div>
  );
}

export default Pay;
