import { useState, useRef } from "react";
import Lottie from "lottie-react";
import "../../styles/ApplyToday.css";
import characterAnimation from "../../assets/animations/character.json";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("üëã Hi! I‚Äôll help you fill this form.");

  const lottieRef = useRef();

  const play = (from, to) => {
    lottieRef.current?.playSegments([from, to], true);
  };

  /* ----------------- VALIDATORS ----------------- */
  const nameOnly = (val) => val.replace(/[^a-zA-Z\s]/g, "");
  const numberOnly = (val) => val.replace(/\D/g, "");

  const isValidEmail = (email) =>
    /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    let val = type === "checkbox" ? checked : value;

    /* NAME FIELDS ‚Üí LETTERS ONLY */
    if (["firstName", "middleName", "lastName"].includes(name)) {
      val = nameOnly(val);
    }

    /* NUMBER FIELDS ‚Üí DIGITS ONLY */
    if (["primaryPhone", "cellPhone", "ssn", "zip"].includes(name)) {
      val = numberOnly(val);
    }

    setForm({ ...form, [name]: val });

    /* ----------------- ASSISTANT LOGIC ----------------- */
    switch (name) {
      case "firstName":
        val.length < 2
          ? (setHint("‚ö†Ô∏è First name looks too short"), play(121, 150))
          : (setHint("üòä First name looks good"), play(0, 30));
        break;

      case "lastName":
        setHint("üëç Last name saved");
        play(31, 60);
        break;

      case "email":
        !isValidEmail(val)
          ? (setHint("‚ùå Email format is invalid"), play(121, 150))
          : (setHint("üìß Email looks correct"), play(61, 90));
        break;

      case "confirmEmail":
        val !== form.email
          ? (setHint("‚ùå Emails don‚Äôt match"), play(121, 150))
          : (setHint("‚úÖ Emails match perfectly"), play(61, 90));
        break;

      case "primaryPhone":
        val.length < 10
          ? (setHint("üì± Phone number must be at least 10 digits"), play(121, 150))
          : (setHint("üìû Phone number looks valid"), play(91, 120));
        break;

      case "ssn":
        val.length !== 9
          ? (setHint("‚ö†Ô∏è SSN must be 9 digits"), play(121, 150))
          : (setHint("üîê SSN format looks valid"), play(91, 120));
        break;

      case "consent":
        if (checked) {
          setHint("üéâ Great! You can submit now");
          play(151, 180);
        }
        break;

      default:
        setHint("‚úçÔ∏è Keep going, great job!");
        play(0, 30);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!isValidEmail(form.email)) {
      setMessage("‚ùå Invalid email address");
      play(121, 150);
      return;
    }

    if (form.primaryPhone.length < 10) {
      setMessage("‚ùå Invalid phone number");
      play(121, 150);
      return;
    }

    if (form.email !== form.confirmEmail) {
      setMessage("‚ùå Emails do not match");
      play(121, 150);
      return;
    }

    if (!form.consent) {
      setMessage("‚ùå Consent is required");
      play(121, 150);
      return;
    }

    try {
      setLoading(true);
      setMessage("");
      setHint("üöÄ Submitting your application...");
      play(151, 180);

      const res = await fetch("http://localhost:5000/apply", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message);

      setMessage("üéâ Application submitted successfully!");
      setHint("ü•≥ Your application has been sent successfully!");
      play(181, 210);

    } catch (err) {
      setMessage(`‚ùå ${err.message}`);
      setHint("‚ö†Ô∏è Submission failed. Try again.");
      play(121, 150);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      <h1>Apply Today ‚Äì Join the RGM Family</h1>
      <p>Please complete the application below.</p>

      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        <div className="speech-bubble">{hint}</div>
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>
        {/* FORM CONTENT UNCHANGED */}
      </form>
    </div>
  );
}

export default ApplyToday;
