import { useState, useRef } from "react";
import Lottie from "lottie-react";
import "../../styles/ApplyToday.css";
import characterAnimation from "../../assets/animations/character.json";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("");

  const lottieRef = useRef();

  // Fields required to complete form
  const requiredFields = [
    "firstName",
    "lastName",
    "ssn",
    "dob",
    "license",
    "address1",
    "city",
    "state",
    "zip",
    "primaryPhone",
    "email",
    "confirmEmail"
  ];

  const remainingCount = () =>
    requiredFields.filter((f) => !form[f]).length;

  const play = (seg) => lottieRef.current?.playSegments(seg, true);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === "checkbox" ? checked : value;

    setForm({ ...form, [name]: val });

    // Default attention movement
    play([0, 20]);

    switch (name) {
      case "firstName":
        val ? play([0, 30]) : play([121, 150]);
        setHint(val ? "Nice! First name looks good." : "Please enter your first name.");
        break;

      case "lastName":
        val ? play([31, 60]) : play([121, 150]);
        setHint(val ? "Great! Last name done." : "Last name is required.");
        break;

      case "email":
        if (!val.includes("@")) {
          play([121, 150]);
          setHint("Hmm‚Ä¶ email doesn‚Äôt look correct.");
        } else {
          play([61, 90]);
          setHint("Email looks good üëç");
        }
        break;

      case "confirmEmail":
        if (val !== form.email) {
          play([121, 150]);
          setHint("Emails do not match.");
        } else {
          play([61, 90]);
          setHint("Emails match perfectly!");
        }
        break;

      case "primaryPhone":
        if (val.length < 10) {
          play([121, 150]);
          setHint("Phone number seems too short.");
        } else {
          play([91, 120]);
          setHint("Phone number looks valid üìû");
        }
        break;

      case "consent":
        if (checked) {
          play([151, 180]);
          setHint("Awesome! You‚Äôre almost done üéâ");
        } else {
          play([121, 150]);
          setHint("Consent is required to submit.");
        }
        break;

      default:
        setHint(`Filling ${name.replace(/([A-Z])/g, " $1")}`);
    }

    // Progress feedback
    const left = remainingCount();
    if (left > 0) {
      setTimeout(() => {
        setHint(`You‚Äôre doing great! ${left} field${left > 1 ? "s" : ""} left.`);
      }, 600);
    } else {
      play([151, 180]);
      setHint("Everything looks good! Ready to submit üöÄ");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      setMessage("‚ùå Emails do not match!");
      play([121, 150]);
      setHint("Please fix the email mismatch.");
      return;
    }

    if (!form.consent) {
      setMessage("‚ùå Please give consent!");
      play([121, 150]);
      setHint("Consent is required.");
      return;
    }

    try {
      setLoading(true);
      play([151, 180]);
      setHint("Submitting your application‚Ä¶");

      const res = await fetch("http://localhost:5000/apply", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      if (!res.ok) throw new Error();

      setMessage("üéâ Application submitted successfully!");
      play([181, 210]);
      setHint("Success! We‚Äôll contact you soon üòä");

    } catch {
      setMessage("‚ùå Something went wrong. Try again.");
      play([121, 150]);
      setHint("Submission failed. Please retry.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      <h1>Apply Today ‚Äì Join the RGM Family</h1>

      <p>
        Thank you for your interest in <strong>RGM Family</strong>. Please
        complete the application below. Incomplete applications may delay
        processing.
      </p>

      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        {hint && <div className="speech-bubble">{hint}</div>}
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>
        {/* YOUR FORM CONTENT REMAINS UNCHANGED */}
      </form>
    </div>
  );
}

export default ApplyToday;
